INSERT INTO `tbl_content` (`id`, `code`, `cate_id`, `title`, `description`, `content`, `image`, `create_at`, `display_img_detail`, `status`) VALUES
(1, 1689360590, 2, 'Câu truyện về SVK Herbal', 'Thiên nhiên ban tặng cho đất nước Việt thảm thực vật trù phú với nhiều loài thảo dược quý hiếm. Trong dân gian lại có hàng ngàn bài thuốc hay đực lưu truyền, hàng trăm bác sỹ, dược sỹ và lương y đầy k', '<p><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&acirc;u truyện về SVK Herbal</strong></span></samp></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Thi&ecirc;n nhi&ecirc;n ban tặng cho đất nước Việt thảm thực vật tr&ugrave; ph&uacute; với nhiều lo&agrave;i thảo dược qu&yacute; hiếm.&nbsp;Trong d&acirc;n gian lại c&oacute; h&agrave;ng ng&agrave;n b&agrave;i thuốc hay đực lưu truyền, h&agrave;ng trăm b&aacute;c sỹ, dược sỹ v&agrave; lương y đầy kiến thức về y học cổ truyền. Trong l&uacute;c đ&oacute;, trước sự qu&aacute; tải của c&aacute;c bệnh viện, gi&aacute; thuốc t&acirc;y dược cao, thuốc kh&aacute;ng sinh nhiều t&aacute;c hại phụ, m&ocirc;i trường &ocirc; nhiễm như hiện nay, sức khỏe Việt cần phải c&oacute; c&aacute;c sản phẩm tự nhi&ecirc;n, an to&agrave;n với gi&aacute; cả ph&ugrave; hợp cho mọi tầng lớp.</span></samp></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c sự kiện n&agrave;y th&uacute;c đẩy ba anh em ch&uacute;ng t&ocirc;i thực hiện sớm hơn c&aacute;c &yacute; tưởng &ldquo;Thảo dược Việt cho sức khỏe Việt&rdquo; v&agrave; &ldquo;Bảo tồn thảo dược Việt&rdquo; qua sự th&agrave;nh lập của c&ocirc;ng ty SVK Herbal với d&ograve;ng sản phẩm LANUI. Ch&uacute;ng ta cần trở về với cội nguồn đưa sản phẩm thảo dược an to&agrave;n v&agrave;o việc ph&ograve;ng chống v&agrave; điều trị bệnh qua việc nh&acirc;n giống thảo dược thi&ecirc;n nhi&ecirc;n qu&yacute; hiếm, nu&ocirc;i trồng thảo dược dưới phương thức canh t&aacute;c thi&ecirc;n nhi&ecirc;n (naturally grown) v&agrave; hợp t&aacute;c.</span></samp></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><strong>&ldquo;Thảo dược Việt cho sức khỏe Việt&rdquo;</strong></strong></span></samp></span></p>\r\n\r\n<div style=\"text-align:center\">\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Tầm Nh&igrave;n &amp; Sứ Mệnh:</strong></span></samp></span></p>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Tầm nh&igrave;n</strong> của ch&uacute;ng t&ocirc;i l&agrave; đem sản phẩm tư nhi&ecirc;n, an to&agrave;n với gi&aacute; cả hợp l&yacute; v&agrave;o mọi tủ thuốc gia đ&igrave;nh Việt v&agrave; thế giới.<br />\r\n<strong>Sứ Mệnh </strong>của ch&uacute;ng t&ocirc;i</span></samp></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;m ph&aacute;, nghi&ecirc;n cứu v&agrave; sản xuất sản phẩm để k&eacute;o d&agrave;i tuổi thọ của con người</span></samp></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Bảo tồn v&agrave; ph&aacute;t huy c&aacute;c gi&aacute; trị từ c&aacute;c phương thuốc đ&ocirc;ng y phục vụ cho sức khỏe con người.</span></samp></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Bảo tồn những c&acirc;y thảo dược</span></samp></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Chiến Lược</strong></span></samp></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Thảo dược Việt cho sức khỏe người Việt</span></samp></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Bảo tồn chuyển giao c&ocirc;ng nghệ canh n&ocirc;ng bền vững cho v&ugrave;ng nguy&ecirc;n liệu tự nhi&ecirc;n.</span></samp></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Kết hợp c&aacute;c đối t&aacute;c c&ugrave;ng tầm nh&igrave;n v&agrave; c&oacute; thế mạnh bổ sung</span></samp></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Gi&aacute; Trị Cốt L&otilde;i</strong></span></samp></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Cam kết chất lượng sản phẩm v&agrave; dịch vụ ưu việt</span></samp></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng t&ocirc;i c&ocirc;ng nhận để đạt mục ti&ecirc;u kinh doanh, phải thực hiện sự li&ecirc;m ch&iacute;nh, minh bạch, bảo vệ v&agrave; ph&aacute;t triển m&ocirc;i trường bền vững</span></samp></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p style=\"text-align:left\"><span style=\"font-size:14px\"><samp><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng ngừng học hỏi s&aacute;ng tạo v&agrave; ph&aacute;t triển sản phẩm cho tất cả c&aacute;c tầng lớp.</span></samp></span></p>\r\n	</li>\r\n</ul>\r\n</div>\r\n', '1689360590_content_blogs.png', '2023-07-15 01:49:50', 0, 1),
(2, 1689419042, 5, 'Đội ngũ chuyên gia', 'Thiên nhiên ban tặng cho đất nước Việt thảm thực vật trù phú với nhiều loài thảo dược quý hiếm. Trong dân gian lại có hàng ngàn bài thuốc hay được lưu truyền, hàng trăm bác sỹ, lương y đầy kinh nghệm', '<p style=\"text-align:center\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://portalsvk:81/public/images/blogs/ckeditor/1483777412bac-sy-tran-van-nam.jpg\" style=\"height:433px; width:650px\" /></span></p>\r\n\r\n<div style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>B&aacute;c Sỹ Trần Văn Năm</strong><br />\r\n<em><strong>(Chuy&ecirc;n Khoa 2 L&atilde;o khoa, Nguy&ecirc;n Ph&oacute; Viện trưởng điều h&agrave;nh Viện Y Dươc Học D&acirc;n Tộc Tp. HCM)</strong></em></span></div>\r\n\r\n<div style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thực h&agrave;nh chuy&ecirc;n ng&agrave;nh Y Học Cổ Truyền hơn 30 năm,</span></div>\r\n\r\n<div style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&aacute;c đề t&agrave;i đ&atilde; nghi&ecirc;n cứu v&agrave; giảng dạy:</strong></span></div>\r\n\r\n<div style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Bệnh l&yacute; mạn t&iacute;nh kh&ocirc;ng l&acirc;y: Tăng huyết &aacute;p, Vữa xơ động mạch, bệnh Tim&nbsp;thiếu m&aacute;u cục bộ mạn t&iacute;nh,</span></div>\r\n\r\n<div style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Bệnh xương &ndash; khớp: vi&ecirc;m &ndash; tho&aacute;i ho&aacute; khớp, lo&atilde;ng xương người tuổi cao,&nbsp;bệnh xương thuỷ tinh (bệnh Tạo xương bất to&agrave;n), Gout. &Ocirc;ng l&agrave; một trong những người nghi&ecirc;n cứu về cao xương động vật ứng dụng trong điều trị bệnh c&aacute;c bệnh về xương bất to&agrave;n (xương thủy tinh). Gần đ&acirc;y BS.Năm đ&atilde; ứng dụng th&agrave;nh c&ocirc;ng phương ph&aacute;p kết hợp Y học cổ truyền v&agrave; Y học hiện đại trong điều trị bệnh xương thủy tinh cho c&aacute;c trẻ em.</span></div>\r\n\r\n<div style=\"text-align:justify\">&nbsp;</div>\r\n\r\n<div style=\"text-align:center\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://portalsvk:81/public/images/blogs/ckeditor/1483777106thac-sy-co-duc-trong.jpg\" style=\"height:300px; width:400px\" /></span></div>\r\n\r\n<div>\r\n<div class=\"even field-item\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Thạc sy Cổ Đức Trọng</strong></span></div>\r\n\r\n<div class=\"even field-item\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tốt nghiệp Khoa Vạn vật Trường Đại học Khoa học S&agrave;i G&ograve;n. Từ năm 1987 &ocirc;ng bắt đầu nghi&ecirc;n cứu về thảo dược Linh Chi. Tr&ograve;n 30 năm t&igrave;m hiểu v&agrave; nghi&ecirc;n cứu về loại Linh Chi với&nbsp;bộ sưu tầm hơn 50 chủng lo&agrave;i linh chi.</span></div>\r\n</div>\r\n', '1689435447_content_blog.png', '2023-07-15 23:23:57', 1, 1),
(3, 1689438378, 11, 'Thêm một \"Vũ khí\"  chất lượng bảo vệ người bệnh Covid-19', 'Chắc chắn phải lo sợ khi ai đó biết mình nhiễm Sars – CoV – 2, nhưng cũng không nên quá hoảng loạn đến mất ăn mất ngủ. Vì mất ăn mất ngủ, sẽ “hợp tác” với con “Corona quái ác” này tàn phá khủng khiếp Hệ hô hấp và nhiều nội tạng khác của người bệnh, thậm chí dẫn đến tử vong.', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Chắc chắn phải lo sợ khi ai đ&oacute; biết m&igrave;nh nhiễm Sars &ndash; CoV &ndash; 2, nhưng cũng kh&ocirc;ng n&ecirc;n qu&aacute; hoảng loạn đến mất ăn mất ngủ. V&igrave; mất ăn mất ngủ, sẽ &ldquo;hợp t&aacute;c&rdquo; với con &ldquo;Corona qu&aacute;i &aacute;c&rdquo; n&agrave;y t&agrave;n ph&aacute; khủng khiếp Hệ h&ocirc; hấp v&agrave; nhiều nội tạng kh&aacute;c của người bệnh, thậm ch&iacute; dẫn đến tử vong. Hiện nay, tr&ecirc;n Thế giới c&oacute; thuốc vừa đặc trị vừa hỗ trợ chữa bệnh Covid &ndash; 19:</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Y Học Phương T&acirc;y:&nbsp;Vaccin c&aacute;c loại, oxy v&agrave; ECMO, thuốc diệt Sars-CoV-2 (Remdesivir), Kh&aacute;ng thể đơn d&ograve;ng (Tocilizumab), khuốc kh&aacute;ng vi&ecirc;m (Dexamethsone), nhiều loại kh&aacute;ng sinh, Ibuprofen (Advil, gi&uacute;p giảm triệu chứng trong những ng&agrave;y đầu nhiễm bệnh)&hellip; thuốc hứa hẹn: Ronapreve, Molnupiravir&hellip;</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Y Học Phương Đ&ocirc;ng:&nbsp;c&aacute;c dược liệu gi&uacute;p cải thiện chức năng Hệ miễn dịch như: Nh&acirc;n s&acirc;m, nấm Linh chi, Đinh lăng, Ho&agrave;ng kỳ, Bạch phục linh, Bạch truật&hellip;; dược liệu c&oacute; t&iacute;nh kh&aacute;ng vi khuẩn &ndash; virus, kh&aacute;ng vi&ecirc;m, được nghi&ecirc;n cứu (in vitro, in vivo, in silico) như:&nbsp;<em>Kim ng&acirc;n hoa, Li&ecirc;n kiều, Xuy&ecirc;n t&acirc;m li&ecirc;n, Diếp c&aacute;, Rễ Cam thảo; c&aacute;c loại thảo dược c&oacute; chứa tinh dầu gi&uacute;p kh&aacute;ng khuẩn, lo&atilde;ng đờm&hellip;l&agrave;m sạch v&agrave; th&ocirc;ng tho&aacute;ng đường h&ocirc; hấp: Trần b&igrave;, Hạnh nh&acirc;n, Ma ho&agrave;ng, C&aacute;t c&aacute;nh&hellip;</em></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tuy nhi&ecirc;n, ch&uacute;ng ta kh&ocirc;ng n&ecirc;n bỏ qu&ecirc;n một &ldquo;Vũ&nbsp;kh&iacute; chất lượng&rdquo; gi&uacute;p người bệnh Covid &ndash; 19 tho&aacute;t qua ranh giới sống &ndash; chết:&nbsp;<em>SỨC KHỎE HỆ TI&Ecirc;U H&Oacute;A.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>1. Bằng chứng của mối li&ecirc;n hệ giữa hệ H&ocirc; hấp v&agrave; Ti&ecirc;u h&oacute;a:</em></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Theo B&aacute;o Health Day, Thứ Năm ng&agrave;y 14/5/ 2020: Corona virus kh&ocirc;ng chỉ tấn c&ocirc;ng Phổi, n&oacute; c&ograve;n g&acirc;y tổn thương Hệ ti&ecirc;u h&oacute;a, đặc biệt ở những người bệnh thuộc thể bệnh nặng, dựa v&agrave;o khảo s&aacute;t h&igrave;nh ảnh Cộng hưởng từ của Ruột, cho thấy những tổn thương do nghẽn c&aacute;c mạch m&aacute;u nhỏ của ruột gi&agrave; (đại tr&agrave;ng) g&acirc;y hoại tử th&agrave;nh ruột (<em>Dr. Rajesh Bhayana, thuộc Khoa chẩn đo&aacute;n h&igrave;nh ảnh của BV Massachusetts General &ndash; Boston.</em></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Một số nghi&ecirc;n cứu cho thấy: c&aacute;c thụ thể chức năng (Functional receptors) c&oacute; t&ecirc;n ACE 2 (Angiotensin-Converting Enzyme 2 = ACE2) nơi m&agrave; SARS &ndash; CoV-2 gắn kết, từ đ&oacute; x&acirc;m nhập v&agrave;o cơ thể. ACE-2 c&oacute; ở ni&ecirc;m mạc mũi &ndash; họng, thanh &ndash; phế quản, phổi, mắt, ruột non, ruột gi&agrave;, thận, l&aacute;ch, tụy, gan v&agrave; n&atilde;o. Quan trọng l&agrave; c&aacute;c thụ thể n&agrave;y c&oacute; nhiều ở Hệ ti&ecirc;u h&oacute;a, đặc biệt l&agrave; ruột gi&agrave;, cao hơn gấp 100 lần ở hệ H&ocirc; hấp. N&ecirc;n khi Sars-CoV-2 tấn c&ocirc;ng kh&ocirc;ng phải chỉ c&oacute; triệu chứng ở hệ h&ocirc; hấp (như: ho, đau họng, kh&oacute; thở, khạc đ&agrave;m&hellip;), m&agrave; c&ograve;n biểu hiện tr&ecirc;n Hệ ti&ecirc;u h&oacute;a (n&ocirc;n, buồn n&ocirc;n, đầy bụng, ch&aacute;n ăn, ti&ecirc;u chảy). [<em>The American Journal of Gastroenterology:&nbsp;July 2020 &ndash; Volume 115 &ndash; Issue 7 &ndash; p 1003-1006].</em></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Phần lớn tế b&agrave;o ni&ecirc;m mạc đường H&ocirc; hấp v&agrave; đường Ruột c&oacute; c&ugrave;ng chức năng bảo vệ cơ thể qua cơ chế: tiết chất nh&agrave;y, cung cấp yếu tố miễn dịch, nhằm kiểm so&aacute;t sự tấn c&ocirc;ng của yếu tố g&acirc;y bệnh. Theo China&rsquo;s National Health Commission, Versin 60, ghi nhận: người bệnh Covid-19, giảm r&otilde; rệt lợi khuẩn (Probiotic) như Lactobacillus, Bifidobacterium, giảm hấp thu một số amine acid (tryptophan) g&acirc;y n&ecirc;n c&aacute;c rối loạn ti&ecirc;u h&oacute;a &ndash; hấp thu ở ruột, tăng phản ứng vi&ecirc;m&hellip;g&acirc;y ảnh hưởng hoạt động của ni&ecirc;m mạc Hệ h&ocirc; hấp v&agrave; ống ti&ecirc;u h&oacute;a.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>2. Vai tr&ograve; của Lợi khuẩn, chất Xơ v&agrave; Sức đề kh&aacute;ng:</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">C&aacute;c nh&agrave; Khoa học cho biết, c&oacute; 70% th&agrave;nh phần li&ecirc;n quan Hệ miễn dịch nằm ở Ống ti&ecirc;u h&oacute;a.</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Probiotic ở ruột c&oacute; t&aacute;c dụng: kiểm so&aacute;t hại khuẩn, bảo vệ to&agrave;n vẹn tế b&agrave;o ni&ecirc;m mạc ruột; điều h&ograve;a đ&aacute;p ứng miễn dịch, chống vi&ecirc;m; t&aacute;c động 2 chiều tr&ecirc;n trục n&atilde;o &ndash; ruột (Gut &ndash; Brain Axis): ti&ecirc;u h&oacute;a tốt, tinh thần thoải m&aacute;i, giảm lo &acirc;u v&agrave; ngược lại.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Chất xơ c&oacute; lợi cho sức khỏe đường ruột: c&oacute; 2 nh&oacute;m chất xơ tan v&agrave; kh&ocirc;ng tan. Xơ tan (l&ecirc;n men được), tạo ra acid b&eacute;o chuỗi ngắn (SCFA, Short &ndash; Chain Fatty Acid) gi&uacute;p lập lại c&acirc;n bằng hệ vi sinh ở ruột; Xơ kh&ocirc;ng tan (kh&ocirc;ng hoặc kh&oacute; l&ecirc;n men): cải thiện khối ph&acirc;n, độ đồng nhất v&agrave; độ nhờn, g&oacute;p phần hạn chế sự tấn c&ocirc;ng của hại khuẩn v&agrave;o ni&ecirc;m mạc ruột.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sự l&agrave;nh lặn của ni&ecirc;m mạc ống ti&ecirc;u h&oacute;a, c&ugrave;ng với c&aacute;c loại Enzyme ti&ecirc;u h&oacute;a (từ tuyến nước bọt, acid dạ d&agrave;y, dịch tụy, dịch mật) gi&uacute;p hấp thu tốt c&aacute;c chất dinh dưỡng, vitamin, nguy&ecirc;n tố vi lượng, kho&aacute;ng chất&hellip;tạo n&ecirc;n tế b&agrave;o bạch cầu, lympho b&agrave;o v&agrave; kh&aacute;ng thể, tăng cường bảo vệ cơ thể.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>3. L&agrave;m g&igrave; để loại &ldquo;Vủ Kh&iacute;&rdquo;n&agrave;y hoạt động tốt nhất:</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em>Nguy&ecirc;n tắc:</em>&nbsp;chọn thức ăn dễ ti&ecirc;u h&oacute;a &ndash; hấp thu, chế biến đơn giản, bổ sung đủ chất xơ v&agrave; lợi khuẩn; cần hạn chế thức ăn chế biến sẵn.</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Hạn chế thịt đỏ (b&ograve;, heo), chất b&eacute;o b&atilde;o h&ograve;a (mỡ động vật) hoặc chuyển dạng (Trans-fat = dầu mỡ đun qu&aacute; s&ocirc;i, hay sử dụng nhiều lần): v&igrave; cơ thể phải tốn nhiều enzyme để chuyển h&oacute;a ch&uacute;ng, trong khi nguồn enzyme n&agrave;y c&oacute; giới hạn, v&agrave; giảm trong khi bệnh nặng hoặc tuổi cao. Nhiều chất b&eacute;o v&agrave; thịt đỏ c&ograve;n g&acirc;y qu&aacute; tải cho Gan mật, biểu hiện tăng Men gan.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Giảm tối đa đường trắng (đường tinh chế): v&igrave; g&acirc;y tăng phản ứng vi&ecirc;m, đề kh&aacute;ng insulin&hellip;</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ăn nhiều rau &ndash; tr&aacute;i c&acirc;y (chiếm 70% khẩu phần), đa dạng m&agrave;u sắc: v&igrave; cung cấp chất xơ, kẽm, selenium, vitamin (C, A, E&hellip;), &beta;-caroten gi&uacute;p tăng sức đề kh&aacute;ng.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Bổ sung lợi khuẩn: nh&oacute;m Bacillus subtilis, Bifidobacterium lactic&hellip;từ c&aacute;c thức ăn truyền thống như: Yogurt, Natto (c&aacute;c loại đậu ủ với enzyme natto), Miso, Sauerkraut, Kim chi&hellip; hay c&aacute;c Chế phẩm bổ sung.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Mỗi bữa ăn n&ecirc;n c&oacute; v&agrave;i loại sau đ&acirc;y:&nbsp;<em>Tỏi, Nghệ, H&agrave;nh, Gừng, Sả, L&aacute; mơ l&ocirc;ng, rau thơm, nấm m&egrave;o đen, Artichoke, Đu đủ, Dứa thơm&hellip;</em>rất tốt cho hoạt động của Hệ ti&ecirc;u h&oacute;a v&agrave; điều h&ograve;a qu&aacute; tr&igrave;nh đ&ocirc;ng m&aacute;u.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Uống đủ nước sẽ tốt cho tuần ho&agrave;n m&aacute;u &ndash; dịch thể, tăng nu&ocirc;i dưỡng tế b&agrave;o ni&ecirc;m mạc, hỗ trợ hoạt động của enzyme được tối ưu.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tập thể dục, thở đ&uacute;ng: thở bụng (Thở cơ ho&agrave;nh), giữ mở thanh quản sau khi h&iacute;t v&agrave;o sẽ cung cấp đủ oxy tế b&agrave;o, gi&uacute;p c&acirc;n bằng hệ thần kinh, điều h&ograve;a nhu động ruột.</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Kiểm so&aacute;t stress: tĩnh t&acirc;m, hướng thiện, thiền định&hellip;gi&uacute;p Ruột &ndash; n&atilde;o tiết đủ Serotonin (chất nội tiết hạnh ph&uacute;c).</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Kh&ocirc;ng uống rượu bia, thuốc l&aacute;, nước ngọt c&oacute; gaz,</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ngủ đủ, nghỉ ngơi hợp l&yacute;: t&aacute;i tạo năng lượng, gi&uacute;p bảo tồn hệ enzyme trong cơ thể.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em><strong>Lời kết:</strong></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><em><strong>Qua theo d&otilde;i một số trường hợp c&oacute; phản ứng nhẹ sau khi ti&ecirc;m ph&ograve;ng Vaccin v&agrave; / hoặc bị nhiễm Covid &ndash; 19 được chăm s&oacute;c tại nh&agrave; (một số trong Khu c&aacute;ch ly), ch&uacute;ng t&ocirc;i đ&atilde; hướng dẫn&nbsp;c&aacute;ch ăn đ&uacute;ng, t&iacute;ch cực chăm s&oacute;c tốt sức khỏe đường ruột bằng bổ sung chất xơ v&agrave; lợi khuẩn, ổn định tinh thần.&nbsp;Kết quả bước đầu ghi nhận: hầu hết những người được theo d&otilde;i, c&oacute; thể lực phục hồi tốt, cải thiện r&otilde; những biểu hiện ch&aacute;n ăn, giảm vị gi&aacute;c, buồn n&ocirc;n&hellip;tăng c&acirc;n v&agrave; x&eacute;t nghiệm kiểm tra Sars-CoV &ndash; 2 &acirc;m t&iacute;nh. Hướng dẫn chăm s&oacute;c n&agrave;y ph&ugrave; hợp với lời khuy&ecirc;n của nhiều thầy thuốc c&oacute; kinh nghiệm:&nbsp;&ldquo;Tỳ thổ của Đ&ocirc;ng y (tương đương&nbsp;Hệ Ti&ecirc;u H&oacute;a) l&agrave; nguồn gốc ph&aacute;t sinh mu&ocirc;n vật, n&ecirc;n giữ Tỳ Thổ mạnh, mọi bệnh sẽ lui&rdquo; hay &ldquo;Tỳ thổ bệnh cả 12 kinh mạch đều bệnh&rdquo;&nbsp;[Nội Kinh]. Do đ&oacute;, chăm s&oacute;c Sức khỏe Hệ ti&ecirc;u h&oacute;a hoạt động tốt nhất, l&agrave;&nbsp;&ldquo;Vủ kh&iacute;&rdquo;&nbsp;chủ lực cần cho bệnh nh&acirc;n Covid &ndash; 19 n&oacute;i ri&ecirc;ng v&agrave; nhiều bệnh kh&oacute; chữa trị kh&aacute;c n&oacute;i chung.</strong></em></span></span></p>\r\n', '1689438378_content_blogs.png', '2023-07-16 00:11:26', 0, 1),
(4, 1689438642, 11, 'Sự thật về tác dụng của Glucosamine', 'Glucosamine, một chất bổ sung tư nhiên thường được bán trên thị trường để trị đau khớp, không có tác dụng gì hơn một viên thuốc giả, theo một nghiên cứu mới cho biết. ', '<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Glucosamine, một chất bổ sung tư nhi&ecirc;n thường được b&aacute;n tr&ecirc;n thị trường để trị đau khớp, kh&ocirc;ng c&oacute; t&aacute;c dụng g&igrave; hơn một vi&ecirc;n thuốc giả, theo một nghi&ecirc;n cứu mới cho biết.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ph&acirc;n t&iacute;ch c&aacute;c dữ liệu đ&atilde; được c&ocirc;ng bố &nbsp;từ c&aacute;c cuộc thử nghiệm l&acirc;m s&agrave;ng cho thấy ở cả hai nh&oacute;m theo d&otilde;i sau 3 th&aacute;ng v&agrave; sau 24 th&aacute;ng l&agrave; glucosamine kh&ocirc;ng c&oacute; ảnh hưởng g&igrave; đến việc giảm đau ở h&ocirc;ng hay đầu gối do chứng vi&ecirc;m khớp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ngay cả những kết quả ph&acirc;n t&iacute;ch từ c&aacute;c nh&oacute;m nhỏ - những đối tượng tham gia nghi&ecirc;n cứu như nh&oacute;m thừa c&acirc;n hoặc nh&oacute;m bị vi&ecirc;m nặng - đều kh&ocirc;ng thấy được lợi &iacute;ch g&igrave; từ chất bổ sung n&agrave;y.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">T&aacute;c giả nghi&ecirc;n cứu đề t&agrave;i cao cấp Sita Bierma-Zeinstra thuộc Trung t&acirc;m Y tế Đại học Erasmus ở Rotterdam, H&agrave; Lan cho biết: &quot;Hầu hết c&aacute;c c&ocirc;ng bố gần đ&acirc;y nhất đều kết luận rằng glucosamine thiếu hiệu quả. Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i biết rằng vi&ecirc;m xương khớp c&oacute; thể g&acirc;y ảnh hưởng kh&aacute;c nhau cho từng tiểu nh&oacute;m bệnh nh&acirc;n vi&ecirc;m khớp&rdquo;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">B&aacute;o c&aacute;o gần đ&acirc;y nhất từ Trung t&acirc;m Thống k&ecirc; Y tế Quốc gia Hoa Kỳ cho thấy người Mỹ đ&atilde; chi gần 13 tỷ đ&ocirc; la v&agrave;o năm 2012 cho c&aacute;c sản phẩm bổ sung tự nhi&ecirc;n v&agrave; glucosamine l&agrave; một trong những loại phổ biến nhất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Hiệp hội Nghi&ecirc;n cứu Vi&ecirc;m Xương khớp Quốc tế, Viện Sức khoẻ &amp; Chăm s&oacute;c Y tế Quốc gia Hoa Kỳ cũng đ&atilde; c&ocirc;ng bố việc thiếu chứng cứ để c&ocirc;ng nhận glucosamine l&agrave; một phương &aacute;n chữa trị hữu hiệu cho vi&ecirc;m khớp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">&ldquo;Tuy nhi&ecirc;n, trước khi ch&uacute;ng t&ocirc;i đưa b&eacute; ra khỏi một dung dịch chữa trị v&agrave; đổ dung dịch đ&oacute;, điều quan trọng l&agrave; phải biết liệu c&aacute;c nh&oacute;m thay thế kh&aacute;c c&oacute; thể c&oacute; hiệu quả hay kh&ocirc;ng &quot;: Bierma-Zeinstra n&oacute;i với Reuters Health bằng email.</span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">C&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; ph&acirc;n t&iacute;ch dữ liệu từ c&aacute;c cuộc thử nghiệm l&acirc;m s&agrave;ng được diễn ra từ năm 1994 đến năm 2014. Trong tổng số 21 bộ hồ sơ nghi&ecirc;n cứu t&igrave;m thấy li&ecirc;n quan đến đề t&agrave;i n&agrave;y, chỉ c&oacute; 6 bộ hồ sơ chia sẻ kết quả qua OA Trial Bank, một trung t&acirc;m th&acirc;u thập cơ sỡ dữ liệu từ c&aacute;c thử nghiệm l&acirc;m s&agrave;ng tr&ecirc;n to&agrave;n cthế giới cho b&ecirc;nh thấp khớp (OA &ndash; osteoarthritis). Trong 6 bộ hồ sơ n&agrave;y, c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; ghi nhận rằng kh&ocirc;ng c&oacute; một thử nghiệm n&agrave;o được t&agrave;i trợ bởi c&aacute;c c&ocirc;ng ty sản xuất glucosamine.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Năm trong số 6 thử nghiệm l&acirc;m s&agrave;ng n&agrave;y, bao gồm tr&ecirc;n 1,600 bệnh nh&acirc;n, so s&aacute;nh hiệu quả giữa glucosamine v&agrave; giả dược. Năm trong 6 thử nghiệm tập trung v&agrave;o vi&ecirc;m khớp đầu gối, v&agrave; một v&agrave;o vi&ecirc;m khớp h&ocirc;ng</span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Nh&igrave;n chung, t&aacute;c dụng của glucosamine v&agrave; giả dược về giảm đau v&agrave; năng độ hoạt động cơ thể, trong thời gian ngắn hoặc d&agrave;i hạn (một hoặc hai năm sau đ&oacute;) kh&ocirc;ng kh&aacute;c nhau. Chất bổ sung n&agrave;y cũng kh&ocirc;ng hiệu quả hơn so với giả dược - ứng dụng ở c&aacute;c tiểu nh&oacute;m nghi&ecirc;n cứu dựa tr&ecirc;n mức độ đau, mức độ nghi&ecirc;m trọng của vi&ecirc;m xương khớp, tuổi t&aacute;c, chỉ số mỡ trong cơ thể dựa tr&ecirc;n chiều cao &amp; c&acirc;n nặng, giới t&iacute;nh hoặc dấu hiệu vi&ecirc;m.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">&quot;Tất nhi&ecirc;n, điều đ&aacute;ng n&oacute;i nhất trong nghi&ecirc;n cứu n&agrave;y l&agrave; khi c&oacute; được nh&agrave; thương mại t&agrave;i trợ, việc chia sẻ dữ liệu trở n&ecirc;n rất kh&oacute; khăn&quot;, b&agrave; Bierma-Zeinstra n&oacute;i. &quot;Việc cho ph&eacute;p truy cập v&agrave;o dữ liệu của c&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng, mặc d&ugrave; đ&atilde; được lan truyền bởi nhiều tổ chức nghi&ecirc;n cứu tr&ecirc;n to&agrave;n thế giới nhưng vẫn c&ograve;n xa lạ với phổ biến thực tế&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ngo&agrave;i ra, c&aacute;c nh&agrave; nghi&ecirc;n cứu kh&ocirc;ng c&ograve;n t&igrave;m thấy được dữ liệu của một nghi&ecirc;n cứu năm 2006. Dẫu rằng dữ liệu của c&aacute;c nghi&ecirc;n cứu cũ c&oacute; thể bị x&oacute;a bỏ, việc n&agrave;y thường kh&ocirc;ng xảy ra cho c&aacute;c nghi&ecirc;n cứu gần đ&acirc;y, b&agrave; Bierma-Zeinstra n&oacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Nh&oacute;m nghi&ecirc;n cứu dự kiến cập nhật dữ liệu theo nh&oacute;m nhỏ trong OA Trial Bank năm năm một lần. Họ sẽ tiếp tục li&ecirc;n lạc với c&aacute;c nh&agrave; nghi&ecirc;n cứu thử nghiệm l&acirc;m s&agrave;ng để khuyến kh&iacute;ch họ đ&oacute;ng g&oacute;p dữ liệu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">&ldquo;C&aacute;c nghi&ecirc;n cứu trong tương lai n&ecirc;n xem x&eacute;t kỹ hơn về vi&ecirc;m xương khớp gối, khớp h&ocirc;ng v&agrave; c&aacute;c loại bổ sung cụ thể như glucosamine sulfate so với glucosamine hydrochloride&rdquo;: nh&oacute;m của b&agrave; Bierma-Zeinstra viết trong Bi&ecirc;n ni&ecirc;n sử c&aacute;c bệnh thấp khớp.</span></span></p>\r\n</div>\r\n</div>\r\n', '1689438642_content_blogs.jpg', '2023-07-16 00:11:44', 0, 1),
(5, 1689443860, 12, 'LANUI TEA Slim - Giảm Cân ', 'Thân hình cân đối làm cuộc sống năng động, tự tin. Để đảm bảo việc giảm cân được an toàn, Trà LANUI® Giảm Cân – Slim Tea được chế biến từ nấm qúy linh chi đỏ kết hợp với một vài thảo dược tự nhiên của Việt Nam. ', '<div>\r\n<div>\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#2ecc71\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">LANUI&reg;Slim Tea - Giảm C&acirc;n </span></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&ldquo;Th&acirc;n h&igrave;nh c&acirc;n đối, năng động cuộc sống&rdquo;</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Th&acirc;n h&igrave;nh c&acirc;n đối l&agrave;m cuộc sống năng động, tự tin. Để đảm bảo việc giảm c&acirc;n được an to&agrave;n, Tr&agrave; LANUI&reg; Giảm C&acirc;n &ndash; Slim Tea được chế biến từ nấm q&uacute;y linh chi đỏ kết hợp với một v&agrave;i thảo dược tự nhi&ecirc;n của Việt Nam.</span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">C&Ocirc;NG DỤNG </span></span></strong></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tr&agrave; LANUI&reg; Giảm C&acirc;n &ndash; Slim Tea gi&uacute;p cải thiện t&igrave;nh trạng rối loạn t&iacute;ch tụ chất b&eacute;o qua nhiều cơ chế:</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">K&iacute;ch th&iacute;ch đốt ch&aacute;y mỡ thừa gi&uacute;p giảm c&acirc;n, l&agrave;m săn chắc cơ bắp</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Cải thiện qu&aacute; tr&igrave;nh chuyển h&oacute;a lipid, giảm&nbsp;rối loạn lipit m&aacute;u</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tăng cường ti&ecirc;u h&oacute;a, gi&uacute;p cơ thể đ&agrave;o th&agrave;i độc tố</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Tăng cường hoạt động trao đổi chất trong cơ thể</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ph&ograve;ng ngừa c&aacute;c bệnh li&ecirc;n qu&aacute;n đến b&eacute;o ph&igrave; như tim mạch, tiểu đường, tho&aacute;i h&oacute;a khớp gối, suy d&atilde;n hệ tĩnh mạch chi dưới</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><strong>ĐỐI TƯỢNG SỬ DỤNG</strong> </span></span></p>\r\n\r\n<div>\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Người thừa c&acirc;n, b&eacute;o ph&igrave; muốn thu gọn v&oacute;c d&aacute;ng v&agrave; cải thiện sức khỏe</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ph&ugrave; hợp sử dụng cho tất cả đối tượng, kể cả trẻ em (giảm liều so với người lớn). </span></span></p>\r\n	</li>\r\n</ul>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Người cao huyết &aacute;p, gan nhiễm mỡ, rối loạn mỡ m&aacute;u</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">&nbsp;Đ&aacute;i th&aacute;o đường, bệnh tim do mạch v&agrave;nh&hellip;</span></span></p>\r\n	</li>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">TH&Agrave;NH PHẦN </span></span></strong></p>\r\n</div>\r\n</div>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sản phẩm LANUI của C&ocirc;ng ty CP SVK Herbal được&nbsp; h&igrave;nh th&agrave;nh&nbsp; dựa tr&ecirc;n cơ sở l&yacute; luận của nền Y học cổ truyền Việt Nam v&agrave; kết quả điều trị thực tế của c&aacute;c thầy thuốc c&oacute; kinh nghiệm trong việc kh&aacute;m v&agrave; chữa bệnh. </span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><em><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Linh Chi Đỏ: </span></span><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Gi&uacute;p tăng cường hoạt động trao đổi chất, k&iacute;ch th&iacute;ch qu&aacute; tr&igrave;nh đốt ch&aacute;y mỡ thừa, từ đ&oacute; giảm được sự h&igrave;nh th&agrave;nh của c&aacute;c tế b&agrave;o mỡ</span></span></em></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><em><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">L&aacute; Sen: Trong l&aacute; sen c&oacute; nhiều flavonoid, chất n&agrave;y c&oacute; t&aacute;c dụng chống rối loạn Cholesterol, gi&uacute;p giảm mỡ, giảm c&acirc;n, l&agrave;m săn chắc cơ thể. </span></span></em></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><em><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sơn Tra: Gi&uacute;p tăng lượng enzym trong dạ d&agrave;y, v&agrave; chứa nhiều acid hữu cơ như acid citric, acid tartric, vitamin C gi&uacute;p ti&ecirc;u h&oacute;a chất b&eacute;o tốt hơn.</span></span></em></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">LỜI KHUY&Ecirc;N CỦA CHUY&Ecirc;N GIA </span></span></strong></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Chứng b&eacute;o ph&igrave; chỉ c&oacute; một số &iacute;t xuất ph&aacute;t từ bệnh l&yacute; (bệnh hệ nội tiết, do thuốc, di truyền), c&ograve;n lại đa phần do lối sống như &iacute;t vận động, ăn thừa năng lượng, đối diện nhiều stress trong cuộc sống.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Khi kết hợp Tr&agrave; LANUI&reg; Giảm C&acirc;n &ndash; Slim Tea với việc tập thể dục đều đặn, thực hiện lao động v&agrave; nghỉ ngơi c&acirc;n bằng, kiểm so&aacute;t stress, ăn uống ki&ecirc;ng hơn như giảm thịt, &iacute;t chất b&eacute;o, giảm đường, hạn chế thức ăn nhiều năng lượng, sẽ đạt hiệu quả giảm c&acirc;n nhanh hơn v&agrave; an to&agrave;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ngo&agrave;i ra, kh&ocirc;ng n&ecirc;n thực hiện giảm c&acirc;n qu&aacute; nhanh như nhịn ăn qu&aacute; độ, uống thuốc tẩy sổ, thuốc g&acirc;y ch&aacute;n ăn. Giảm c&acirc;n nhanh bằng nhịn ăn, tẩy sổ, lợi tiểu mạnh, c&oacute; thể g&acirc;y mất nước v&agrave; x&aacute;o trộn c&acirc;n bằng c&aacute;c chất điện giải trong cơ thể. Như vậy sẽ c&oacute; &nbsp; ảnh hưởng kh&ocirc;ng tốt đến sức khoẻ chung như mệt mỏi, giảm chất lượng sống, giảm năng suất lao động. </span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Số ĐK: 13058/2017/ATTP-XNCB </span></span></strong></em></p>\r\n\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Ch&uacute; &yacute;: Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc v&agrave; &nbsp; kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh </span></span></strong></em></p>\r\n\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sản phẩm ph&aacute;t huy t&aacute;c dụng khi được sử dụng li&ecirc;n tục </span></span></strong></em></p>\r\n\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sản phẩm của: C&Ocirc;NG TY CỔ PHẦN SVK HERBAL </span></span></strong></em></p>\r\n\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Địa chỉ: 1D/16 Bình Giã, Phường 13, Quận T&acirc;n B&igrave;nh, TP. Hồ Ch&iacute; Minh</span></span></strong></em></p>\r\n\r\n<p><em><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Điện thoại: 84.8.7302.2266</span></span></strong></em></p>\r\n\r\n<p><a href=\"https://lanui.vn/tra-thao-duoc-giam-can-ha-mo-mau-lanu-slim-tea\" target=\"_blank\"><img alt=\"\" src=\"http://portalsvk:81/public/images/blogs/ckeditor/button.png\" style=\"height:49px; width:179px\" /></a></p>\r\n</div>\r\n</div>\r\n', '1689443860_content_blogs.png', '2023-07-16 01:05:32', 0, 1),
(6, 1689444879, 12, 'LANUI Cleanse Tea - Trà thải độc', 'Ô nhiễm môi trường, thực phẩm sử dụng hằng ngày tích tụ nhiều độc tố gây cơ thể mệt mỏi, tinh thần sa sút. Đây là mầm mống của nhiều căn bệnh tim mạch, gan, phổi, đặc biệt là ung thư.', '<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&Ocirc; nhiễm m&ocirc;i trường, thực phẩm sử dụng hằng ng&agrave;y t&iacute;ch tụ nhiều độc tố g&acirc;y cơ thể mệt mỏi, tinh thần sa s&uacute;t. Đ&acirc;y l&agrave; mầm mống của nhiều căn bệnh tim mạch, gan, phổi, đặc biệt l&agrave; ung thư.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Thanh lọc cơ thể mỗi ng&agrave;y&rdquo; với tr&agrave; thảo dược LANUI&reg; Th&acirc;n An &ndash; CLEANSE Tea được chiết suất 100% từ thảo dược thi&ecirc;n nhi&ecirc;n gi&uacute;p đ&agrave;o thải c&aacute;c chất cặn b&atilde; ra khỏi cơ thể.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lượng độc tố được t&iacute;ch tụ l&acirc;u ng&agrave;y trong cơ thể&nbsp;tấn c&ocirc;ng tế b&agrave;o khiến cho tế b&agrave;o bị tổn thương/đột biến, nguy&ecirc;n nh&acirc;n của nhiều căn bệnh tim mạch, gan, phổi, đặc biệt l&agrave; ung thư </strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&Ocirc;NG DỤNG </strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&agrave; thảo dược LANUI&reg; Thải Độc &ndash; Cleanse Tea gi&uacute;p đ&agrave;o thải c&aacute;c độc tố ra khỏi cơ thể qua c&aacute;c cơ chế:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gan: Chịu tr&aacute;ch nhiệm lọc c&aacute;c độc tố trong m&aacute;u, chuyển h&oacute;a ch&uacute;ng th&agrave;nh c&aacute;c chất &iacute;t độc hơn để đ&agrave;o thải ra khỏi cơ thể qua thận, da, phổi v&agrave; ruột. Cỏ mực c&oacute; trong th&agrave;nh phần tr&agrave; c&oacute; chức năng k&iacute;ch th&iacute;ch l&agrave;m tăng hoạt t&iacute;nh của c&aacute;c enzyme thải độc gan, bảo vệ tế b&agrave;o gan, kh&aacute;ng khuẩn</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thận: C&oacute; chức năng như một bộ lọc trao đổi chất, đ&agrave;o thải độc tố ra khỏi m&aacute;u, điều h&ograve;a độ pH của cơ thể (kh&ocirc;ng qu&aacute; kiềm hoặc acid). Thận thải độc th&ocirc;ng qua việc tạo ra nước tiểu. C&aacute;c th&agrave;nh phần thảo dược trong Lanui Cleanse l&agrave;m tăng lượng nước tiểu gi&uacute;p đ&agrave;o thải độc tố qua đường thận</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mật: L&agrave; một chất sền sệt m&agrave;u v&agrave;ng hơi xanh lục, c&oacute; vị đắng. Mật g&oacute;p phần quan trọng v&agrave;o qu&aacute; tr&igrave;nh ti&ecirc;u ho&aacute; thức ăn, ph&acirc;n huỷ c&aacute;c chất b&eacute;o, th&uacute;c đẩy cho hoạt động của c&aacute;c men lipase (ph&acirc;n huỷ lipid). Ch&uacute;ng c&ograve;n gi&uacute;p cho c&aacute;c chất b&eacute;o đ&atilde; được ti&ecirc;u ho&aacute; c&oacute; thể đi qua được th&agrave;nh ruột.</span></span></p>\r\n\r\n<p><em><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ tăng cường chức năng gan: Giải độc gan, bảo vệ tế b&agrave;o gan, kh&aacute;ng khuẩn </span></span></em></p>\r\n\r\n<p><em><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lợi mật: K&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;; ti&ecirc;u ho&aacute; thức ăn nhiều đạm, dầu mỡ.</span></span></em></p>\r\n\r\n<p><em><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhuận trường: Điều ho&agrave; nhu động v&agrave; tiết dịch tại ni&ecirc;m mạc đại tr&agrave;ng, chống t&aacute;o b&oacute;n.</span></span></em></p>\r\n\r\n<p><em><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lợi tiểu:&nbsp; Thanh lọc thận hiệu quả</span></span></em></p>\r\n\r\n<p><em><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hạ sốt, chống vi&ecirc;m, diệt khuẩn: Hạn chế ph&aacute;t triển vi khuẩn &ndash; si&ecirc;u vi khuẩn qua đường h&ocirc; hấp.</span></span></em></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Hỗ trợ điều trị hiệu quả một số diện bệnh: Vi&ecirc;m đường tiểu, vi&ecirc;m đường mật, sốt do si&ecirc;u vi (sốt xuất huyết Dengue, sốt ph&aacute;t ban).</em></span></span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">TH&Agrave;NH PHẦN</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kim ng&acirc;n hoa: Tăng b&agrave;i tiết dịch vị v&agrave; mật, gi&uacute;p thanh lọc cơ thể, chống độc, hạ nhiệt. </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Rau m&aacute;: Lợi mật, chống dị ứng, kh&aacute;ng sinh, chống vi&ecirc;m. </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bồ c&ocirc;ng anh: T&aacute;c dụng lợi tiểu, giải độc v&agrave; nhuận tr&agrave;ng, tăng độ lọc thận hiệu quả </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cỏ mực : Giải độc gan, bảo vệ tế b&agrave;o gan, kh&aacute;ng khuẩn, giải độc tố, cầm m&aacute;u, lợi tiểu. </span></span></p>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gừng : K&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, chống dị ứng, kh&aacute;ng vi&ecirc;m, khử độc. </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạch mao căn : Hạ nhiệt, ti&ecirc;u ứ huyết, lợi tiểu tiện, cầm m&aacute;u </span></span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">LỜI KHUY&Ecirc;N CHUY&Ecirc;N GIA </span></span></strong></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Thức ăn v&agrave; kh&ocirc;ng kh&iacute; bị &ocirc; nhiễm khiến cho cơ thể ch&uacute;ng ta phải hứng chịu rất nhiều độc tố, lượng độc tố qu&aacute; lớn vượt qu&aacute; khả năng đ&agrave;o thải của hệ thống thải độc. Độc tố sẽ t&iacute;ch tụ tại c&aacute;c m&ocirc;, tế b&agrave;o trong cơ thể, l&agrave;m mất kiểm so&aacute;t c&aacute;c gốc tự do, c&aacute;c gốc tự do n&agrave;y tấn c&ocirc;ng tế b&agrave;o khiến cho tế b&agrave;o bị tổn thương/đột biến. Đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n của nhiều căn bệnh như tim mạch, gan, phổi, đặc biệt l&agrave; ung thư. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Xu hướng của thế giới hiện nay l&agrave; con người h&atilde;y quay về với thi&ecirc;n nhi&ecirc;n, sống th&acirc;n thiện với m&ocirc;i trường, bảo vệ sinh th&aacute;i. Ăn uống những thực phẩm, nước uống thuần khiết c&oacute; nguồn gốc thi&ecirc;n nhi&ecirc;n, ưu ti&ecirc;n thực vật. Chế biến thức ăn đơn giản, hạn chế đường, muối, bột n&ecirc;m. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Sử dụng dược phẩm (dược liệu) từ thi&ecirc;n nhi&ecirc;n . Ho&aacute; dược chỉ d&agrave;nh cho những trường hợp nguy kịch, cần được giải quyết nhanh. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\">Huy động mọi tiềm năng ph&ograve;ng chống bệnh của cơ thể: tập dưỡng sinh, Yoga, thiền định, thở s&acirc;u, bơi lội, chay bộ&hellip; Chủ động kiểm tra sức khoẻ định kỳ, ti&ecirc;m ph&ograve;ng vaccine đối với c&aacute;c bệnh l&acirc;y nhiễm. Kh&ocirc;ng h&uacute;t thuốc l&aacute;, kh&ocirc;ng lạm dụng rượu, bia v&agrave; c&aacute;c chất k&iacute;ch th&iacute;ch. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:14px\"><a href=\"https://lanui.vn/vien-uong-lanui-cleanse-than-an-thai-doc-gan\" target=\"_blank\"><img alt=\"\" src=\"http://portalsvk:81/public/images/blogs/ckeditor/button.png\" style=\"height:49px; width:179px\" /></a></span></span></p>\r\n</div>\r\n', '1689444920_content_blog.png', '2023-07-16 01:21:57', 0, 1);
INSERT INTO `tbl_content` (`id`, `code`, `cate_id`, `title`, `description`, `content`, `image`, `create_at`, `display_img_detail`, `status`) VALUES
(7, 1689445568, 12, 'LANUI Mạch An – Cardio Tea', 'Nhồi máu cơ tim, xơ vữa động mạch, huyết áp cao, đột quy là những bệnh tim mạch phổ biến và rất nguy hiểm. Tuy nhiên, các dấu hiệu bệnh giai đoạn đầu khó nhận biết và thường dễ bị bỏ qua. Sử dụng trà thảo dược LANUI® Mach An CARDIO Tea hằng ngày để phòng ngừa và bảo vệ tim luôn khỏe mạnh. Sống khỏe với trái tim khỏe! ', '<div>\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&agrave; Thảo Dược LANUI&reg; MẠCH AN &ndash; CARDIO TEA </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Tim khỏe, Sống khỏe&rdquo; </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhồi m&aacute;u cơ tim, xơ vữa động mạch, huyết &aacute;p cao, đột quy l&agrave; những bệnh tim mạch phổ biến v&agrave; rất nguy hiểm. Tuy nhi&ecirc;n, c&aacute;c dấu hiệu bệnh giai đoạn đầu kh&oacute; nhận biết v&agrave; thường dễ bị bỏ qua. Sử dụng tr&agrave; thảo dược LANUI&reg; Mach An CARDIO Tea hằng ng&agrave;y để ph&ograve;ng ngừa v&agrave; bảo vệ tim lu&ocirc;n khỏe mạnh. Sống khỏe với tr&aacute;i tim khỏe! </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bệnh tim mạch kh&ocirc;ng do một nguy&ecirc;n nh&acirc;n duy nhất m&agrave; c&oacute; nhiều yếu tố g&oacute;p phần g&acirc;y bệnh như: </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuổi cao </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">H&uacute;t thuốc l&aacute;, lạm dụng rượu bia. &nbsp; </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Huyết &aacute;p cao, bệnh tiểu đường </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thừa c&acirc;n, b&eacute;o ph&igrave; </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Rối loạn lipid m&aacute;u (cholesterol cao, TG cao, LDL cao, HDL thấp) </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&Iacute;t vận động, t&acirc;m thể nhiều stress. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ăn uống nhiều chất b&eacute;o xấu; sử dụng nhiều đường. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Yếu tố di truyền</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&Ocirc;NG DỤNG </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ổn định mỡ trong m&aacute;u, chống gốc tự do, hạn chế h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ổn định huyết &aacute;p </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&ograve;ng bệnh tim do xơ vữa động mạch: nhồi m&aacute;u cơ tim, tai biến mạch m&aacute;u n&atilde;o, tắc mạch m&aacute;u chi. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ổn định đường huyết (tăng cường chuyển ho&aacute; chất bột , đường) </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện c&aacute;c triệu chứng l&atilde;o ho&aacute; của bệnh thần kinh, chống suy giảm tr&iacute; nhớ, ph&ograve;ng bệnh Alzheimer </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">TH&Agrave;NH PHẦN</p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Linh chi đỏ: T&aacute;c dụng ổn định huyết &aacute;p được d&ugrave;ng cho cả người huyết &aacute;p cao v&agrave; người huyết &aacute;p thấp. Gi&uacute;p giảm lượng cholesterrol</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sơn tra: Gi&uacute;p tăng lượng enzym trong dạ d&agrave;y, gi&uacute;p ti&ecirc;u h&oacute;a tốt hơn, ổn định mỡ trong m&aacute;u.</span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nấm M&egrave;o đen: Chống oxyt ho&aacute;, chống kết tụ tiểu cầu, hạn chế h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng, ổn định đường huyết, cải thiện hoạt động hệ tim mạch, tăng cường chuyển ho&aacute; c&aacute;c chất, bảo vệ gan, cải thiện tuần ho&agrave;n m&aacute;u cho thần kinh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hoa h&ograve;e: Ổn định huyết &aacute;p, kh&aacute;ng khuẩn, chống vi&ecirc;m, chống co thắt cơ trơn ở đường ruột </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nghệ v&agrave;ng: Chưa hoạt chất l&agrave; curcumin (chiếm 60%) c&oacute; t&aacute;c dụng: chống oxyt ho&aacute;, kh&aacute;ng vi&ecirc;m, kh&aacute;ng khuẩn, giảm cholesterol v&agrave; lipid m&aacute;u to&agrave;n phần, l&agrave;nh vết lo&eacute;t, tăng cường lưu th&ocirc;ng m&aacute;u </span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">ĐỐI TƯỢNG SỬ DỤNG &ndash; LIỀU D&Ugrave;NG </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Người huyết &aacute;p cao, huyết &aacute;p kh&ocirc;ng ổn định </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bệnh tim thiếu m&aacute;u cục bộ (bệnh mạch v&agrave;nh, cơn đau thắt ngực) </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Rối loạn tuần ho&agrave;n n&atilde;o (rối loạn tiền đ&igrave;nh, thiếu m&aacute;u n&atilde;o), </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ecirc; tay ch&acirc;n do m&aacute;u lưu th&ocirc;ng k&eacute;m. </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Liều d&ugrave;ng : 1 &ndash; 2 t&uacute;i ng&agrave;y 2 &ndash; 3 lần, ng&acirc;m nước s&ocirc;i uống. </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">LỜI KHUY&Ecirc;N CHUY&Ecirc;N GIA </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thực hiện lối sống l&agrave;nh mạnh: tập thể dục vừa sức thường xuy&ecirc;n, kh&ocirc;ng h&uacute;t thuốc l&aacute;, hạn chế tối đa rượu bia, sống c&acirc;n bằng giữa lao động v&agrave; nghỉ ngơi; biết c&aacute;ch kiểm so&aacute;t stress (thiền định, tập thở, thư gi&atilde;n). </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thực h&agrave;nh ăn uống khoa học: bữa ăn nhiều rau củ quả sạch v&agrave; c&aacute;c loại ngũ cốc, hạn chế thịt đỏ, thức ăn chế biến &iacute;t muối, giảm sử dụng đường tinh chế. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Định kỳ được kh&aacute;m sức khoẻ để ph&aacute;t hiện bệnh sớm. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sống ph&ugrave; hợp với thi&ecirc;n nhi&ecirc;n , chọn sử dụng c&aacute;c loại thực vật vừa l&agrave; rau vừa c&oacute; t&aacute;c dụng l&agrave;m thuốc. C&aacute;c loại rau quả c&oacute; m&agrave;u đậm (xanh, v&agrave;ng, đỏ đậm m&agrave;u) v&igrave; chứa nhiều vitamin, chất kho&aacute;ng v&agrave; c&oacute; vai tr&ograve; antioxidant hiệu quả. </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://lanui.vn/tra-thao-duoc-lanui-mach-an-giam-mo-mau-dieu-hoa-huyet-ap-detail-product7-html\" target=\"_blank\"><img alt=\"\" src=\"http://portalsvk:81/public/images/blogs/ckeditor/button.png\" style=\"height:49px; width:179px\" /></a></p>\r\n</div>\r\n</div>\r\n', '1689445568_content_blogs.jpg', '2023-07-17 00:23:46', 0, 1),
(8, 1689524613, 12, 'LANUI Tiêu hóa - Digest Tea', '“Sức khỏe tốt bắt nguồn từ hệ tiêu hóa tốt”. Sử dụng Trà bảo vệ sức khỏe LANUI Tiêu Hóa DIGEST Tea hàng ngày để tối ưu việc hấp thụ các chất dinh dưỡng từ thức ăn đưa vào và dễ dàng đào thải các chất cặn bả ra khỏi cơ thể. ', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sản phẩm LANUI của C&ocirc;ng ty CP SVK Herbal được h&igrave;nh th&agrave;nh dựa tr&ecirc;n cơ sở l&yacute; luận của nền Y học cổ truyền Việt Nam v&agrave; kết quả điều trị thực tế của c&aacute;c thầy thuốc c&oacute; kinh nghiệm trong việc kh&aacute;m v&agrave; chữa bệnh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Sức khỏe tốt bắt nguồn từ hệ ti&ecirc;u h&oacute;a tốt&rdquo;. Sử dụng Tr&agrave; bảo vệ sức khỏe LANUI Ti&ecirc;u H&oacute;a DIGEST Tea h&agrave;ng ng&agrave;y để tối ưu việc hấp thụ c&aacute;c chất dinh dưỡng từ thức ăn đưa v&agrave;o v&agrave; dễ d&agrave;ng đ&agrave;o thải c&aacute;c chất cặn bả ra khỏi cơ thể. </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&Ocirc;NG DỤNG </span></span></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">K&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, gi&uacute;p ăn ngon miệng </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chống đầy &ndash; s&ocirc;i bụng, chậm ti&ecirc;u, ợ hơi </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện hấp thu c&aacute;c chất từ dạ d&agrave;y &ndash; ruột, hạn chế ti&ecirc;u chảy &nbsp; </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chống mệt mỏi </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>T<span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">H&Agrave;NH PHẦN</span></span></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gừng: K&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, chống dị ứng, kh&aacute;ng vi&ecirc;m, khử độc</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sơn tra: Gi&uacute;p tăng lượng enzym trong dạ d&agrave;y; Sownt ra chứa nhiều acid hữu cơ như acid citric, acid tartric, vitamin C, gi&uacute;p ti&ecirc;u h&oacute;a chất dầu mỡ tốt hơn</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trần b&igrave;: K&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, kh&aacute;ng vi&ecirc;m, chống lo&eacute;t,chống dị ứng, lợi mật</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">H&ograve;a sơn: Cải thiển chức năng ti&ecirc;u h&oacute;a, chống ti&ecirc;u chảy, hạn chế mất nước; tăng cường chức năng một số cơ quan kh&aacute;c như phổi, thận</span></span></li>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ĐỐI TƯỢNG SỬ DỤNG &ndash; LIỀU D&Ugrave;NG</strong> </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ti&ecirc;u ho&aacute; k&eacute;m, sống ph&acirc;n, rối loạn ti&ecirc;u ho&aacute; (ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n) </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầy chướng bụng, ăn kh&ocirc;ng ngon, </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Buồn n&ocirc;n, tiết nhiều nh&agrave;y đờm ở họng v&agrave;o buổi s&aacute;ng </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Liều d&ugrave;ng : 1 &ndash; 2 t&uacute;i ng&agrave;y 2 &ndash; 3 lần, ng&acirc;m nước s&ocirc;i uống. </span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">LỜI KHUY&Ecirc;N CHUY&Ecirc;N GIA </span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguy&ecirc;n tắc để hệ ti&ecirc;u h&oacute;a lu&ocirc;n khỏe mạnh: </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ăn một c&aacute;ch tập trung: kh&ocirc;ng n&ecirc;n xem tivi hay điện thoại trong l&uacute;c ăn. N&ecirc;n chuẩn bị đủ thời gian cho c&aacute;c bữa ăn để kh&ocirc;ng phải ăn vội v&atilde;, v&igrave; khi ăn cần nhai kỹ cho đến khi thức ăn nhuyễn v&agrave; n&ecirc;n ăn miếng nhỏ sẽ tốt cho hệ ti&ecirc;u h&oacute;a. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ăn đ&uacute;ng giờ: Giờ giấc bữa ăn rất quan trọng đến sức khỏe cũng như v&oacute;c d&aacute;ng của bạn. Thời điểm l&yacute; tưởng cho bữa s&aacute;ng l&agrave; từ khoảng 6-8h s&aacute;ng v&agrave; bạn kh&ocirc;ng n&ecirc;n ăn sau 7 giờ tối. Ăn trước khoảng thời gian n&agrave;y sẽ gi&uacute;p ti&ecirc;u h&oacute;a thức ăn hiệu quả nhất, đồng thời loại bỏ t&igrave;nh trạng thức ăn chuyển h&oacute;a th&agrave;nh mỡ thừa. H&atilde;y cố gắng ăn đ&uacute;ng giờ mỗi ng&agrave;y để tạo th&oacute;i quen cho cơ thể, gi&uacute;p hệ ti&ecirc;u h&oacute;a hoạt động ổn định hơn. </span></span></p>\r\n</div>\r\n</div>\r\n', '1689524613_content_blogs.jpg', '2023-07-16 23:23:33', 0, 1),
(9, 1689526256, 12, 'LANUI Tâm An - Stress Free', '\"Giải lao trí não” đem lại cân bằng và hưng phấn cho cuộc sống. Trà Thảo Dược Lanui® Tâm An – Stress Free chứa các hoạt chất sinh học có trong những thảo dược giúp thư giãn, giảm căng thẳng và mệt mỏi. ', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Tra Thảo Dược LANUI&reg; T&Acirc;M AN &ndash; STRESS FREE </strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Giải lao tr&iacute; n&atilde;o&rdquo; đem lại c&acirc;n bằng v&agrave; hưng phấn cho cuộc sống. Tr&agrave; Thảo Dược Lanui&reg; T&acirc;m An &ndash; Stress Free chứa c&aacute;c hoạt chất sinh học c&oacute; trong những thảo dược gi&uacute;p thư gi&atilde;n, giảm căng thẳng v&agrave; mệt mỏi. </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&Ocirc;NG DỤNG </span></span></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thư gi&atilde;n, giảm căng thẳng v&agrave; mệt mỏi </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ ngủ ngon giấc </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chống rối loạn cảm x&uacute;c (lo buồn, c&aacute;u gắt, thiếu vui th&iacute;ch trong cuộc sống) </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ điều trị tăng huyết &aacute;p, chống rối loạn nhịp tim </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">TH&Agrave;NH PHẦN</span></span></strong></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&acirc;m sen : </span></span><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&ograve;ng chống rối loạn nhịp tim, cải thiện t&igrave;nh trạng thiếu m&aacute;u cơ tim v&agrave; cải thiện lưu lượng tuần ho&agrave;n động mạch v&agrave;nh </span></span></p>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lạc ti&ecirc;n: T&aacute;c dụng an thần, chữa tim hồi hộp, dịu thần kinh, hạ nhiệt, giải độc, lợi tiểu </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chỉ thực: Th&agrave;nh phần chủ yếu Neohesperidin c&oacute; t&aacute;c dụng tăng lưu lượng m&aacute;u tới n&atilde;o, tim, ổn định thần kinh tim.</span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">ĐỐI TƯỢNG SỬ DỤNG &ndash; LIỀU D&Ugrave;NG </span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Người l&agrave;m việc căng thẳng; mất ngủ, ngủ kh&ocirc;ng s&acirc;u giấc; rối loạn nhịp tim (tim l&uacute;c nhanh l&uacute;c chậm, mất nhịp); đau đầu ch&oacute;ng mặt, mệt mỏi. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thận trọng: phụ nữ c&oacute; thai, tham khảo &yacute; kiến thầy thuốc trước khi sử dụng </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Liều d&ugrave;ng : 1 &ndash; 2 t&uacute;i ng&agrave;y 2 &ndash; 3 lần, ng&acirc;m nước s&ocirc;i uống. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">LỜI KHUY&Ecirc;N CHUY&Ecirc;N GIA </span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi t&acirc;m trạng kh&ocirc;ng ổn định, hưng phấn - ức chế mất c&acirc;n bằng, xung đột trong cuộc sống gia đ&igrave;nh &ndash; x&atilde; hội, nếu kh&ocirc;ng kiểm so&aacute;t được sẽ g&acirc;y lo, sợ, buồn, ch&aacute;n, uất, hận. C&aacute;c cảm x&uacute;c tr&ecirc;n ảnh hưởng xấu đến cơ thể: </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Tăng catecholamine g&acirc;y giảm tế b&agrave;o T, n&ecirc;n cơ thể dễ bị nhiễm tr&ugrave;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Ph&oacute;ng th&iacute;ch histamine, th&uacute;c đẩy co thắt phế quản nặng ở người bị hen phế quản.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Tăng nguy cơ đ&aacute;i th&aacute;o đường, đặc biệt ở người thừa c&acirc;n, v&igrave; stress t&acirc;m l&yacute; biến đổi nhu cầu insulin v&agrave; tăng insulin trong m&aacute;u.</span></span></p>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<ul>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Thay đổi nồng độ acid dịch vị, dẫn tới lo&eacute;t dạ d&agrave;y, tr&agrave;o ngược dạ d&agrave;y thực quản (Gerd&rsquo;s)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Th&uacute;c đẩy xơ vữa động mạch sớm, đặc biệt nếu kết hợp với lối sống &iacute;t vận động &nbsp;hoặc chế độ ăn nhiều mỡ b&atilde;o ho&agrave;</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sự li&ecirc;n hệ giữa stress v&agrave; bệnh c&oacute; căn nguy&ecirc;n t&acirc;m l&yacute; m&agrave; hậu quả sẽ l&agrave; trầm cảm hoặc t&acirc;m thần ph&acirc;n liệt. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ng&agrave;y c&agrave;ng c&oacute; bằng chứng khoa học của mối li&ecirc;n quan nh&acirc;n &ndash; quả giữa thay đổi hệ thống miễn dịch v&agrave; sự ph&aacute;t triển bệnh ung thư. Những nghi&ecirc;n cứu mới đ&acirc;y t&igrave;m thấy mối li&ecirc;n hệ giữa stress, bệnh ung bướu v&agrave; sự ức chế tế b&agrave;o diệt tự nhi&ecirc;n (natural killer), tế b&agrave;o n&agrave;y chịu tr&aacute;ch nhiệm ph&ograve;ng ngừa di căn v&agrave; diệt tế b&agrave;o di căn giai đoạn sớm. </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Để kiểm so&aacute;t stress ch&uacute;ng ta cần c&aacute;c liệu ph&aacute;p từ thực dược (thức ăn nước uống), sinh dược (c&acirc;y-con l&agrave;m thuốc), ho&aacute; dược (t&acirc;n dược), tập luyện thể lực v&agrave; cũng kh&ocirc;ng thể bỏ qu&ecirc;n một liệu ph&aacute;p gi&uacute;p ổn định v&agrave; c&acirc;n bằng hệ thần kinh đ&oacute; l&agrave; liệu ph&aacute;p Thư gi&atilde;n &ndash; Thiền định . Ch&uacute;ng t&ocirc;i tạm gọi đ&oacute; l&agrave; liệu ph&aacute;p &ldquo;T&acirc;m dược&rdquo;. </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n', '1689526256_content_blogs.jpg', '2023-07-16 23:50:56', 0, 1),
(10, 1689526459, 12, 'LANUI Xương Khớp - JOINTS TEA', 'Tăng sức mạnh xương, khớp và làm chậm quá trình thoái hóa xương khớp là vấn đề cần quan tâm của mọi lứa tuổi. Sử dụng trà thảo dược LANUI®XƯƠNG KHỚP với 100% thảo dược tự nhiên hằng ngày là nguồn dinh dưỡng bổ sung hiệu quả cho xương khớp, làm chậm lão hóa, tăng sự dẻo dai, giảm thiểu các triệu chứng đau nhức. Với một cơ thể dẻo dai, không ngại tuổi tác!', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tăng sức mạnh xương, khớp v&agrave; l&agrave;m chậm qu&aacute; tr&igrave;nh tho&aacute;i h&oacute;a xương khớp l&agrave; vấn đề cần quan t&acirc;m của mọi lứa tuổi. Sử dụng tr&agrave; thảo dược LANUI&reg;XƯƠNG KHỚP với 100% thảo dược tự nhi&ecirc;n hằng ng&agrave;y l&agrave; nguồn dinh dưỡng bổ sung hiệu quả cho xương khớp, l&agrave;m chậm l&atilde;o h&oacute;a, tăng sự dẻo dai, giảm thiểu c&aacute;c triệu chứng đau nhức. Với một cơ thể dẻo dai, kh&ocirc;ng ngại tuổi t&aacute;c! </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&Ocirc;NG DỤNG</strong> </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gi&uacute;p chống vi&ecirc;m, giảm đau xương khớp cấp v&agrave; mạn t&iacute;nh </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hạn chế tổn thương sụn, đĩa đệm đốt sống, </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gi&uacute;p tăng sức mạnh d&acirc;y chằng, g&acirc;n cơ </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ điều trị v&ocirc;i h&oacute;a đốt sống, vi&ecirc;m &amp; tho&aacute;i h&oacute;a khớp </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ điều trị vi&ecirc;m khớp do thấp (vi&ecirc;m đa khớp, vi&ecirc;m khớp phản ứng) </span></span></p>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN</strong></p>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hy Thi&ecirc;m : </span></span><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trị đau lưng, mỏi gối do thấp </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cỏ xước: T&aacute;c dụng giảm đau, chống khớp, tăng độ chắc g&acirc;n &ndash; d&acirc;y chằng </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thi&ecirc;n ni&ecirc;n kiện: Hỗ trợ điều trị c&aacute;c chứng t&ecirc; đau mỏi xương khớp, đặc biệt ở người cao tuổi. Sử dụng tốt cho người bị vi&ecirc;m &ndash; tho&aacute;i ho&aacute; xương khớp, lo&atilde;ng xương. </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cỏ ngọt: Tạo vị ngọt tự nhi&ecirc;n, gi&uacute;p ổn định lượng đường &ndash; mỡ trong m&aacute;u&nbsp; </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cốt to&aacute;i bổ: T&aacute;c dụng tăng cường sự hấp thu calci của xương, n&acirc;ng cao lượng phospho v&agrave; calci trong m&aacute;u gi&uacute;p cho ch&oacute;ng liền xương. C&oacute; t&aacute;c dụng giảm đau v&agrave; an thần. </span></span></p>\r\n</div>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ĐỐI TƯỢNG SỬ DỤNG &ndash; LIỀU D&Ugrave;NG</strong> </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Người bị đau mỏi cột sống, c&aacute;c khớp tứ chi do vi&ecirc;m hay tho&aacute;i h&oacute;a, lo&atilde;ng xương. Liều d&ugrave;ng : 1 &ndash; 2 t&uacute;i ng&agrave;y 2 &ndash; 3 lần, ng&acirc;m nước s&ocirc;i uống. </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>LỜI KHUY&Ecirc;N CHUY&Ecirc;N GIA</strong> </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng &ldquo;tự k&ecirc; toa bốc thuốc&rdquo;</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c thuốc chống vi&ecirc;m, giảm đau Gi&uacute;p tăng lượng enzym trong dạ d&agrave;y c&oacute; nguồn gốc ho&aacute; dược l&agrave; một biện ph&aacute;p điều trị quan trọng v&agrave; hữu hiệu trong c&aacute;c bệnh l&yacute; cơ, xương v&agrave; khớp giai đoạn cấp . Tuy nhi&ecirc;n, đ&acirc;y kh&ocirc;ng phải l&agrave; biện ph&aacute;p l&acirc;u d&agrave;i v&agrave; g&acirc;y nhiều biến chứng. Bệnh nh&acirc;n kh&ocirc;ng n&ecirc;n sử dụng t&ugrave;y tiện thuốc chống vi&ecirc;m, giảm đau t&acirc;n dược m&agrave; kh&ocirc;ng t&igrave;m hiểu kỹ lưỡng về c&ocirc;ng dụng, chức năng hay theo một khuyến c&aacute;o điều trị uy t&iacute;n n&agrave;o. </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Biện ph&aacute;p dinh dưỡng</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&igrave; xương được cấu tạo bởi vừa c&aacute;c chất v&ocirc; cơ (chất kho&aacute;ng) v&agrave; vừa phần hữu cơ (tế b&agrave;o xương, chất nền collagen) n&ecirc;n trong chế độ ăn cần đầy đủ 2 th&agrave;nh phần tr&ecirc;n. </span></span><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bổ sung chất collagen hydrate ho&aacute; (như cao xương động vật v&agrave; một số thực vật ) sẽ chuyển th&agrave;nh c&aacute;c loại collagen 1, 2 v&agrave; 3, tốt cho sự ph&aacute;t triển của xương, cơ, g&acirc;n, d&acirc;y chằng. </span></span><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ti&ecirc;u thụ thực phẩm chứa nhiều calcium như sữa v&agrave; c&aacute;c chế phẩm từ sữa &ldquo;sạch&rdquo;, c&aacute; sardine, quả hạch, hạt hướng dương, đậu hủ, rau xanh, cải củ, dịch &eacute;p quả cam; thực phẩm chứa nhiều vitamin D như trứng, gan, bơ, mỡ c&aacute;, sữa v&agrave; dịch &eacute;p quả cam. Ngo&agrave;i thức ăn, cần tận dụng nguồn vitamin D từ &aacute;nh s&aacute;ng mặt trời: phơi nắng khoảng 15 ph&uacute;t v&agrave;o buổi s&aacute;ng sớm. </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tập thể dục</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cần luyện tập li&ecirc;n tục, đều đặn, kh&ocirc;ng qu&aacute; sức nhưng cũng kh&ocirc;ng qua loa. N&ecirc;n chọn loại h&igrave;nh tập c&oacute; sự huy động nhiều nh&oacute;m cơ / xương c&ugrave;ng tham gia v&agrave; c&oacute; kh&aacute;ng lực như chạy bộ, yoga, bơi lội, aerobic, khi&ecirc;u vũ, tập giữ thăng bằng &hellip; </span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kết hợp thở s&acirc;u v&agrave; đ&uacute;ng (kh&ocirc;ng n&iacute;n thở) trong l&uacute;c tập luyện để cơ thể nhận đủ oxy đến từng tế b&agrave;o. </span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n', '1689526459_content_blogs.jpg', '2023-07-16 23:54:19', 0, 1),
(11, 1689526682, 3, ' Hướng Dẫn Lựa Chọn Thực Phẩm Cho Người Tiểu Đường', 'Chế độ ăn đóng vai trò lớn và quyết định thành công trong kiểm soát đường huyết. Tuy nhiên, không dễ dàng để chọn thực phẩm ít gây tăng đường huyết đột ngột đặc biệt là các loại rau – củ - trái cây.', '<div>\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chế độ ăn đ&oacute;ng vai tr&ograve; lớn v&agrave; quyết định th&agrave;nh c&ocirc;ng trong kiểm so&aacute;t đường huyết. Tuy nhi&ecirc;n, kh&ocirc;ng dễ d&agrave;ng để chọn thực phẩm &iacute;t g&acirc;y tăng đường huyết đột ngột đặc biệt l&agrave; c&aacute;c loại rau &ndash; củ - tr&aacute;i c&acirc;y.<br />\r\nC&aacute;c chuy&ecirc;n gia chuy&ecirc;n ng&agrave;nh đ&aacute;i th&aacute;o đường (ĐTĐ) đ&atilde; đề xuất 2 th&ocirc;ng số của c&aacute;c loại thực phẩm cần lưu &yacute; đ&oacute; l&agrave;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- CHỈ SỐ ĐƯỜNG HUYẾT (GLYCEMIC INDEX = GI): chỉ số đường huyết thể hiện tốc &nbsp;độ ti&ecirc;u h&oacute;a v&agrave; hấp thụ c&aacute;c chất đường bột g&acirc;y tăng nhanh lượng đường trong m&aacute;u.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- TẢI LƯỢNG ĐƯỜNG HUYẾT (GLYCEMIC LOAD = GL, được phổ biến năm 1997 bởi Dr. Walter): một th&ocirc;ng số cho biết số lượng v&agrave; chất lượng chất bột đường (carbohydrate) trong một dạng thực phẩm.&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ocirc;ng thức t&iacute;nh GL:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">GL = (GI x lượng carbohydrate c&oacute; trong 100g thực phẩm) / 100.<br />\r\nV&iacute; dụ: Dưa hấu c&oacute; chỉ số đường huyết l&agrave; 72; lượng carbohydrate trong 100g l&agrave; 5. GL sẽ l&agrave; 72 x 5g/100 # 3,6 &ndash; 4. V&igrave; GL của Dưa hấu thấp n&ecirc;n người bệnh ĐTĐ vẫn c&oacute; thể ăn với một lượng vừa phải m&agrave; kh&ocirc;ng sợ ảnh hưởng nhiều đến lượng đường trong m&aacute;u;&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&aacute;o v&agrave; Khoai t&acirc;y c&ugrave;ng c&oacute; GL 30% trọng lượng. Do đ&oacute;, một quả t&aacute;o hoăc một củ khoai t&acirc;y nặng 50 gram th&igrave; sẽ chứa khoảng 15 gram chất đường bột. Với GI l&agrave; 40 v&agrave; GL của quả t&aacute;o l&agrave; 6 gram (40 x 15/100). Nhưng v&igrave; khoai t&acirc;y c&oacute; GI l&agrave; 80, n&ecirc;n c&oacute; GL l&agrave; 12 gram (80 x 15/100). N&oacute;i c&aacute;ch kh&aacute;c, &nbsp;ăn một củ Khoai t&acirc;y th&igrave; t&aacute;c động l&ecirc;n đường huyết sẽ gấp đ&ocirc;i so với ăn một tr&aacute;i t&aacute;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&igrave;n chung, loại rau củ n&agrave;o chứa nhiều nước sẽ c&oacute; GL thấp v&agrave; sẽ &iacute;t g&acirc;y tăng nhanh đường huyết.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong lựa chọn thức ăn, người bệnh đ&aacute;i th&aacute;o đường cần ưu ti&ecirc;n chọn những loại c&oacute; GL v&agrave; GI nằm trong khu vực m&agrave;u xanh (GL từ 0 &ndash; 10; GI từ 0 &ndash; 55), phần c&ograve;n lại l&agrave; m&agrave;u v&agrave;ng (GL từ 11 &ndash; 19; GI từ 56 &ndash; 69) v&agrave; cần hạn chế loại c&oacute; m&agrave;u đỏ (GL từ 20 &ndash; 60; GI từ 70 &ndash; 100). Trừ trường hợp hạ đường huyết khẩn cấp n&ecirc;n chọn loại thức ăn nằm trong v&ugrave;ng m&agrave;u đỏ [PubMed].</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, GI v&agrave; GL chỉ c&oacute; gi&aacute; trị tham khảo. Cũng như thuốc điều trị bệnh, khi uống v&agrave;o cơ thể mỗi người sẽ c&oacute; đ&aacute;p ứng rất kh&aacute;c nhau: c&oacute; hoặc kh&ocirc;ng hiệu quả, thậm ch&iacute; bị dị ứng. C&ugrave;ng một thực phẩm c&oacute; c&ugrave;ng GI cao (v&ugrave;ng m&agrave;u đỏ) kh&ocirc;ng phải ai ăn v&agrave;o đều g&acirc;y tăng nhanh đường huyết như nhau. Do đ&oacute;, ngo&agrave;i GI, GL của thực phẩm, c&ograve;n nhiều th&ocirc;ng số kh&aacute;c chi phối nữa như: c&acirc;n nặng, giới t&iacute;nh, lượng insulin, haemoglobin trong m&aacute;u. N&ecirc;n người bệnh ĐTĐ c&ograve;n cần phải theo d&otilde;i sự cảm nhận, những thay đổi của cơ thể về t&iacute;nh chịu được (dung nạp) với bất cứ loại thức ăn, nước uống n&agrave;o khi đưa v&agrave;o cơ thể.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguy&ecirc;n tắc ch&iacute;nh trong lựa chọn thức ăn của người ĐTĐ l&agrave; ăn với số lượng &iacute;t (hoặc vừa đủ) v&agrave; hợp l&yacute; về tỉ lệ c&aacute;c th&agrave;nh phần: nhiều rau xanh, &iacute;t bột đường v&agrave; chất b&eacute;o, đủ đạm (động &ndash; thực vật).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">GI, GL của một số thực phẩm th&ocirc;ng dụng tại Ch&acirc;u &Aacute;:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguồn: Đơn vị nghi&ecirc;n cứu thực phẩm dinh dưỡng tại Đại học Sydney v&agrave; WHO.</span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">BS. Trần Văn Năm</span></span></p>\r\n</div>\r\n', '1689526682_content_blogs.jpg', '2023-07-17 00:00:28', 0, 1),
(12, 1689526931, 11, ' Bài Thuốc Tăng Cường Sinh Lý Nam', 'Y học đã khẳng định, Testosterone (hay còn gọi là nội tiết tố nam) giữ vai trò “nhạc trưởng” tác động trực tiếp lên 12 cơ chế sinh học của các bộ phận trong cơ thể nam giới, từ sức khỏe nền tảng đến sức khoẻ sinh lý. ', '<div class=\"even field-item\">\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Y học đ&atilde; khẳng định, Testosterone (hay c&ograve;n gọi l&agrave; nội tiết tố nam) giữ vai tr&ograve; &ldquo;nhạc trưởng&rdquo; t&aacute;c động trực tiếp l&ecirc;n 12 cơ chế sinh học của c&aacute;c bộ phận trong cơ thể nam giới, từ sức khỏe nền tảng đến sức khoẻ sinh l&yacute;. Nam giới sau tuổi 40 bắt đầu c&oacute; những suy giảm chức năng sinh l&yacute;, việc sản sinh testosterone giảm dần, ham muốn t&igrave;nh dục giảm theo. Nếu mắc th&ecirc;m c&aacute;c bệnh mạn t&iacute;nh như tăng huyết &aacute;p, tiểu đường, thiểu năng mạch v&agrave;nh, thừa c&acirc;n &ndash; b&eacute;o ph&igrave;, xơ vữa động mạch, gout, ph&igrave; đại tiền liệt tuyến&hellip;c&agrave;ng g&acirc;y t&igrave;nh trạng suy giảm chức năng t&igrave;nh dục trầm trọng hơn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện nay, xu hướng sử dụng c&aacute;c sản phẩm c&oacute; nguồn gốc thảo dược nhằm k&iacute;ch th&iacute;ch cơ thể tự sản sinh testosterone nội sinh, sẽ hiệu quả, an to&agrave;n v&agrave; &iacute;t t&aacute;c dụng phụ. C&aacute;c thảo dược tự nhi&ecirc;n như B&aacute; bệnh (Eurycoma longifolia), Nh&acirc;n s&acirc;m (Panax ginseng), Lộc nhung (Cornu Cervi Parvum), Bạch tật l&ecirc; (Tribulus terrestris)&hellip; c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch cơ thể tăng tiết testosterone, tăng ham muốn, tăng thể lực (bổ thận tr&aacute;ng dương), đ&atilde; được sử dụng c&oacute; kết quả tốt tr&ecirc;n nghi&ecirc;n cứu thực nghiệm v&agrave; l&acirc;m s&agrave;ng. Đặc biệt, khi kết hợp c&aacute;c vị thuốc hợp l&yacute; sẽ đạt được t&aacute;c dụng tốt nhất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&acirc;u nay khi điều trị chức năng sinh l&yacute; suy giảm, c&aacute;c loại thuốc tr&ecirc;n thị trường hiện nay thường d&ugrave;ng thuốc bổ thận tr&aacute;ng dương nhằm kh&ocirc;i phục thận kh&iacute;, cải thiện chức năng t&igrave;nh dục, tăng ham muốn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n &iacute;t ai ch&uacute; &yacute; đến việc d&ugrave;ng thuốc hỗ trợ chức năng tạng can, v&igrave; theo l&yacute; luận đ&ocirc;ng y, tạng can c&oacute; chức năng: &ldquo;Can t&agrave;ng huyết, chủ sơ tiết, can chủ c&acirc;n, khai khiếu ra mắt, ph&ocirc; ra ở mặt&hellip;&rdquo;. Mỗi khi động t&igrave;nh th&igrave; mắt long lanh, mặt đỏ, m&aacute; hồng, &ldquo;đại c&acirc;n&rdquo; cương cứng. Nền tảng của hoạt động t&igrave;nh dục c&oacute; sự g&oacute;p phần quan trọng mắt (nh&igrave;n thấy), d&acirc;m ở mắt, nghĩa l&agrave; do can quản l&yacute;. Sự mạnh mẽ quyết đo&aacute;n thể hiện bản lĩnh của đ&agrave;n &ocirc;ng ch&iacute;nh l&agrave; nhờ &ldquo;to gan lớn mật&rdquo; cũng l&agrave; của chức năng tạng can.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Qua qu&aacute; tr&igrave;nh theo d&otilde;i l&acirc;m s&agrave;ng, sản phẩm Stamina do c&ocirc;ng ty SVK Herbal sản xuất&nbsp;đem đến sự kh&aacute;c biệt so với c&aacute;c loại thuốc bổ v&agrave; thảo dược kh&aacute;c, ch&iacute;nh l&agrave; c&ocirc;ng thức đột ph&aacute; từ sự kết hợp 3 loại thảo dược trong b&agrave;i thuốc tăng cường sinh l&yacute; nam gồm Cao Bạch tật l&ecirc; (Tribulus terrestris), Cao kh&ocirc; Ngũ Vị tử (Schisandra chinensis), Cao Nh&acirc;n s&acirc;m (Panax ginseng).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Bạch tật l&ecirc; c&oacute; c&ocirc;ng dụng: &ldquo;b&igrave;nh can, sơ can, trừ phong, minh mục&rdquo;. Thường được d&ugrave;ng để chữa c&aacute;c bệnh về mắt, đau đầu, mẫn ngứa do chức năng gan suy giảm. Y học cổ truyền Ấn Độ d&ugrave;ng Bạch tật l&ecirc; l&agrave;m thuốc hỗ trợ sinh l&yacute; nam giới, cường dương, cải thiện chức năng sinh l&yacute; tốt hơn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạch tật l&ecirc; được phối hợp với Ngũ vị tử, Nh&acirc;n s&acirc;m gi&uacute;p bổ nguy&ecirc;n kh&iacute;, tăng sức đề kh&aacute;ng, bổ can thận, vừa bảo vệ c&aacute;c tế b&agrave;o gan, hạ men gan do thuốc v&agrave; bia rượu, do vi&ecirc;m gan cấp v&agrave; mạn t&iacute;nh, giải độc gan thận vừa tăng cường chức năng sinh l&yacute; một c&aacute;ch c&oacute; hiệu quả nhất.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><strong>Khi phối b&agrave;i thuốc gồm: Bạch tật l&ecirc;, Nh&acirc;n s&acirc;m v&agrave; Ngũ vị tử sẽ đem lại t&aacute;c dụng hiệp đồng, gi&uacute;p tăng cường thể lực, duy tr&igrave; v&agrave; ổn định sức khoẻ sinh dục nam</strong></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều nghi&ecirc;n cứu về t&aacute;c dụng của to&agrave;n c&acirc;y Bạch tật l&ecirc; c&oacute; chứa saponin steroid, khi thuỷ ph&acirc;n cho ra diosgenin, hecogenin, tigogenin v&agrave; pseudodiosgenin&hellip;c&aacute;c saponin n&agrave;y l&agrave;m tăng tổng hợp testosterone trong cơ thể nam giới hơn 30% l&agrave;m cơ thể cường tr&aacute;ng, gi&agrave;u sinh lực.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c nghi&ecirc;n cứu kh&aacute;c gần đ&acirc;y cũng cho thấy Bạch tật l&ecirc; cũng c&oacute; t&aacute;c dụng chống xơ vữa động mạch, ổn định huyết &aacute;p, ổn định đường huyết, chống l&atilde;o ho&aacute;, tăng cường miễn dịch, k&iacute;ch th&iacute;ch sản sinh tinh tr&ugrave;ng&hellip;Điều n&agrave;y cũng tr&ugrave;ng hợp với vai tr&ograve; của hormon sinh dục nam (testosterone) khi tăng tiết sẽ g&acirc;y tăng ham muốn t&igrave;nh dục, tăng hưng phấn, tăng sự can đảm, hiếu chiến v&agrave; khi giảm tiết sẽ l&agrave;m giảm ham muốn, mệt mỏi, suy nhược, rối loạn cương dương&hellip;</span></span></p>\r\n</div>\r\n', '1689526931_content_blogs.jpg', '2023-07-17 00:02:11', 0, 1);
INSERT INTO `tbl_content` (`id`, `code`, `cate_id`, `title`, `description`, `content`, `image`, `create_at`, `display_img_detail`, `status`) VALUES
(13, 1689527085, 11, 'Nấm mèo đen', 'Dịch chiết Nấm mèo có hai phân đoạn gọi là Auritoxin I và II. Hai phân đoạn này có thành phần hoá học gần giống nhau và chứa Polysaccharide có 93,9% , protein 6,8%. Một nửa polysaccharide có 12,3% là liên kết α và 87,7% là liên kết β và là một heteromanoglucan, trong đó có 45,1% glucose, 43,9% maltose và 11,0% xylose.', '<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ecirc;n La tinh: Auricularia polytricha (Mont.), Sacc. Họ: Mộc nhĩ (Auriculariaceae), Nấm m&egrave;o rất phổ biến ở nhiều nước tr&ecirc;n Thế giới v&igrave; vừa được sử dụng l&agrave;m thuốc vừa l&agrave;m thực phẩm.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Theo Đ&ocirc;ng y: Nấm m&egrave;o c&oacute; vị ngọt v&agrave; t&iacute;nh kh&ocirc;ng n&oacute;ng kh&ocirc;ng lạnh, v&agrave;o 2 kinh Vị v&agrave; Đại trường.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&agrave;nh phần ho&aacute; học: Polysaccharides 10%-30%, hoạt chất ch&iacute;nh l&agrave; tremella polysaccharides. Ngo&agrave;i ra c&ograve;n chứa chất trehalose, pentosan, mannitol, glycogen.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dịch chiết Nấm m&egrave;o c&oacute; hai ph&acirc;n đoạn gọi l&agrave; Auritoxin I v&agrave; II. Hai ph&acirc;n đoạn n&agrave;y c&oacute; th&agrave;nh phần ho&aacute; học gần giống nhau v&agrave; chứa Polysaccharide c&oacute; 93,9% , protein 6,8%. Một nửa polysaccharide c&oacute; 12,3% l&agrave; li&ecirc;n kết &alpha; v&agrave; 87,7% l&agrave; li&ecirc;n kết &beta; v&agrave; l&agrave; một heteromanoglucan, trong đ&oacute; c&oacute; 45,1% glucose, 43,9% maltose v&agrave; 11,0% xylose. Một nửa protein chứa 10 acid amine [Kim Ha Won v&agrave; Cs. CA 119 1993, 175620e). Theo S&aacute;ch C&acirc;y Thuốc V&agrave; Động Vật L&agrave;m Thuốc ở Việt Nam, Tập II, tr. 292]. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&aacute;c dụng:</span></span></p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nấm M&egrave;o chứa th&agrave;nh phần c&oacute; t&aacute;c dụng chống vi&ecirc;m v&agrave; ức chế h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng, giảm cholesterol, ph&ograve;ng bệnh tim do xơ vữa động mạch: nhồi m&aacute;u cơ tim, tai biến mạch m&aacute;u n&atilde;o, tắc mạch m&aacute;u chi.</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chống oxy ho&aacute;, bẫy c&aacute;c gốc tự do, chống l&atilde;o ho&aacute;,</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;ng tế b&agrave;o ung thư, ngưng h&igrave;nh th&agrave;nh c&aacute;c bướu &aacute;c của m&ocirc; li&ecirc;n kết.</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ổn định đường huyết (tăng chuyển ho&aacute; chất mỡ, đường),</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Điều biến miễn dịch, Bảo vệ gan,</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cải thiện c&aacute;c triệu chứng của bệnh thần kinh: Nấm m&egrave;o c&oacute; t&aacute;c dụng bảo vệ chống suy giảm tr&iacute; nhớ, gồm bệnh Alzheimer, qua v&agrave;i con đường gồm ức chế men gắn APP tại vị tr&iacute; b&ecirc;ta (BACE1), m&agrave; chịu tr&aacute;ch nhiệm ph&oacute;ng th&iacute;ch &beta; amyloid peptide c&oacute; độc t&iacute;nh trong n&atilde;o. [PMID: 23662612, PubMed].</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Liều d&ugrave;ng: &nbsp;mỗi ng&agrave;y từ 10 &ndash; 15 g Nấm dạng luộc hoặc bột, hoặc dịch chiết polysaccharide 20% 1000-2000mg mỗi ng&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Độc t&iacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chưa ph&aacute;t hiện độc t&iacute;nh, an to&agrave;n khi sử dụng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gi&aacute; trị dinh dưỡng: (Theo Viện Dinh dưỡng Việt Nam, năm 1995)</span></span></p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i c&aacute;c vitamin v&agrave; protein c&oacute; gi&aacute; trị dinh dưỡng, n&oacute; c&ograve;n chứa c&aacute;c chất kho&aacute;ng K+, Calcium, Mg, Phosphorus, v&agrave; silicon.</span></span></p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&agrave;nh chất dinh dưỡng c&oacute; trong 100g Nấm m&egrave;o:</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Protein</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">10,6 g</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lipid</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">0,2 g</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Glucid</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">65 g</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cellulose</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">7 g</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Na</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">63 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">K</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">856 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ca</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">357 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Fe</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">56,1 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">P</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">201 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&beta;-caroten</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">20 mcg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vitamin B1</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">0,14 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vitamin B2</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">0,55 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vitamin PP</span></span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">2,7 mg</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kinh nghiệm thực tế: nhiều người bệnh tim thiếu m&aacute;u cục bộ do hẹp động mạch v&agrave;nh, rối loạn lipid m&aacute;u, ki&ecirc;n tr&igrave; thực hiện chế độ ăn tăng cường Nấm m&egrave;o kết hợp thay đổi lối sống đ&atilde; cho kết quả cải thiện t&igrave;nh trạng hẹp mạch v&agrave;nh d&aacute;ng kể khi kiểm tra chụp mạch v&agrave;nh.</span></span></p>\r\n</div>\r\n', '1689527085_content_blogs.jpg', '2023-07-17 00:04:45', 0, 1),
(14, 1689527916, 11, 'Kim ngân hoa', 'Các nhà khoa học tìm thấy nước hoa Kim ngân có tác dụng ức chế rất mạnh đối với tụ cầu khuẩn vi khuẩn thương hàn, trùng lỵ, Shiga, tụ cầu vàng, liên cầu khuẩn dung huyết, phế cầu khuẩn, trực khuẩn lî, trực khuẩn ho gà, trực khuẩn thương hàn, trực khuẩn mủ xanh, não cầu khuẩn, trực khuẩn lao ở người . cùng các loại nấm ngoài da, spirochete, virut cúm.', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>T&ecirc;n kh&aacute;c: </strong>Nhẫn đ&ocirc;ng, Ng&acirc;n hoa, Song hoa, Nhị hoa, Boóc kim ng&acirc;̀n (Tày), Chừa giang khằm (Thái), Japanese honeysuckle (Anh), Chèvrefeuille du Japon (Pháp)..</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>T&ecirc;n khoa học:</strong> Lonicera japonica Thunb.; Lonicera dasystyla Rehd.; Lonicera confusa DC.; Lonicera cambodiana Pierre, họ Cơm ch&aacute;y (Caprifoliaceae).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>M&ocirc; tả:&nbsp;</strong>Kim ng&acirc;n l&agrave; loại d&acirc;y leo, th&acirc;n to bằng chiếc đũa d&agrave;i tới 9-10m, c&oacute; nhiều c&agrave;nh, l&uacute;c non m&agrave;u xanh, khi gi&agrave; m&agrave;u đỏ n&acirc;u. L&aacute; h&igrave;nh trứng, mọc đối, phiến l&aacute; rộng 1,5-5cm d&agrave;i 3-8cm. L&aacute; c&acirc;y quanh năm xanh tươi, th&aacute;ng 4-5 c&acirc;y ra hoa đẹp v&agrave; thơm</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Thu h&aacute;i v&agrave; chế biến:</strong> Thu h&aacute;i hoa v&agrave; c&agrave;nh nhưng để ri&ecirc;ng.&nbsp;H&aacute;i hoa v&agrave;o l&uacute;c hoa sắp nở hoặc mới nở, c&ograve;n m&agrave;u trắng chưa chuyển v&agrave;ng. Hoa hay c&agrave;nh khi h&aacute;i về phơi kh&ocirc; hoặc sấy kh&ocirc; l&agrave; d&ugrave;ng được.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Theo y học cổ truyền: </strong>Kim ng&acirc;n hoa c&oacute; vị ngọt, t&iacute;nh h&agrave;n, kh&ocirc;ng độc, v&agrave;o 4 kinh phế, vị, t&acirc;m, tỳ. C&oacute; c&ocirc;ng dụng chữa ung nhọt, ban sởi, m&agrave;y đay, lở ngứa, nhiệt huyết độc lỵ, ho do phế nhiệt, đan độc (vi&ecirc;m quầng), cảm mạo phong nhiệt, &ocirc;n bệnh ph&aacute;t nhiệt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&aacute;ch d&ugrave;ng:</strong>&nbsp;Ng&agrave;y d&ugrave;ng 12 &ndash; 16g. Dạng thuốc sắc, h&atilde;m, cao, vi&ecirc;n. D&ugrave;ng ri&ecirc;ng hay phối hợp với c&aacute;c vị thuốc kh&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Th&agrave;nh phần ho&aacute; học</strong>: flavonoid, saponin l&agrave; 2 chất chủ yếu c&oacute; trong c&acirc;y Kim ng&acirc;n hoa. Hoa v&agrave; l&aacute; chứa flavonoid, chất ch&iacute;nh l&agrave; luteolin-7-rutinosid (lonicerin= scolymosid).Một số chất carotenoid: x-caroten, b-cryptoxanthin, auroxanthin,&nbsp;Acid chlorogenic v&agrave; c&aacute;c đồng ph&acirc;n của n&oacute;.&nbsp;L&aacute; c&oacute; loganin v&agrave; secologanin.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>T&aacute;c dụng dược l&yacute;</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;ng khuẩn: C&aacute;c nh&agrave; khoa học t&igrave;m thấy nước hoa Kim ng&acirc;n c&oacute; t&aacute;c dụng ức chế rất mạnh đối với tụ cầu khuẩn vi khuẩn thương h&agrave;n, tr&ugrave;ng lỵ, Shiga, tụ cầu v&agrave;ng, li&ecirc;n cầu khuẩn dung huyết, phế cầu khuẩn, trực khuẩn l&icirc;, trực khuẩn ho g&agrave;, trực khuẩn thương h&agrave;n, trực khuẩn mủ xanh, n&atilde;o cầu khuẩn, trực khuẩn lao ở người . c&ugrave;ng c&aacute;c loại nấm ngo&agrave;i da, spirochete, virut c&uacute;m.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chống vi&ecirc;m, l&agrave;m giảm chất xuất tiết, giải nhiệt v&agrave; l&agrave;m tăng t&aacute;c dụng thực b&agrave;o của bạch cầu.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&aacute;c dụng hưng phấn trung khu thần kinh cường độ bằng 1/6 của c&agrave; ph&ecirc;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&agrave;m hạ cholesterol trong m&aacute;u.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tăng b&agrave;i tiết dịch vị v&agrave; mật.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>B&agrave;i thuốc c&oacute; Kim ng&acirc;n hoa</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Trị chứng mẩn ngứa, dị ứng: 20 gr hoa Kim ng&acirc;n, Thổ phục linh, Quyết minh tử (sao) mỗi vị 6 gr, Sinh địa, Mạch m&ocirc;n, Ho&agrave;ng đằng mỗi vị 8 gr, Huyền s&acirc;m, Li&ecirc;n kiều mỗi vị 10 gr. Cho 800 ml nước, sắc c&ograve;n 200 ml. Ng&agrave;y d&ugrave;ng một thang, chia uống l&agrave;m ba lần.<br />\r\n&ndash; Chữa mụn nhọt: Ti&ecirc;n phương hoạt mệnh ẩm gồm Kim ng&acirc;n hoa 16g, Trần b&igrave; 8g, Đương quy 12g, Ph&ograve;ng phong 8g, Bạch chỉ 8g, Cam thảo 4g, Bối mẫu 6g, Nhũ hương 4g, Một dược 4g, Thi&ecirc;n hoa phấn 8g, Tạo gi&aacute;c th&iacute;ch 4g, Xuy&ecirc;n sơn miếng. Ng&agrave;y uống 3 lần c&aacute;ch xa bữa ăn 30 ph&uacute;t, uống 2 ng&agrave;y 1 thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa bệnh vảy nến: Ng&acirc;n kiều t&aacute;n (chuyển th&agrave;nh thang) gia giảm gồm Kim ng&acirc;n hoa 16g, Li&ecirc;n kiều 16g, Ngưu b&agrave;ng tử 8g, Kinh giới 12g, Tr&uacute;c diệp 8g, Bạc h&agrave; 6g, Chi tử 6g, quả K&eacute; 8g, Bồ c&ocirc;ng anh 12g, Hạ kh&ocirc; thảo 8g, Thổ phục linh 12g. Ng&agrave;y uống 3 lần c&aacute;ch xa bữa ăn 30 ph&uacute;t, uống 2 ng&agrave;y 1 thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa bệnh tổ đỉa: Bạch ứng ho&agrave;n (chuyển th&agrave;nh thang) gia giảm gồm: Kim ng&acirc;n hoa 16g, Quy vĩ 16g, Li&ecirc;n kiều 12g, Ho&egrave; hoa 8g, Thương truật 12g, quả K&eacute; 12g, Ho&agrave;ng b&aacute; 8g, Đại ho&agrave;ng 6g, Hạ kh&ocirc; thảo 12g, Thổ phục linh 12g, S&agrave;i đất 8g, Bồ kết (đốt tồn t&iacute;nh, bỏ hạt). Ng&agrave;y uống 3 lần c&aacute;ch xa bữa ăn 30 ph&uacute;t, uống 2 ng&agrave;y 1 thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5. Trị cảm sốt: 40 gr hoa kim ng&acirc;n, tr&uacute;c diệp, kinh giới tuệ mỗi vị 16 gr, đạm đậu xị 20 gr, bạc h&agrave;, ngưu b&agrave;ng tử, c&aacute;t c&aacute;nh mỗi vị 24 gr, li&ecirc;n kiều 40 gr. Tất cả mang sấy kh&ocirc;, t&aacute;n bột, ho&agrave;n vi&ecirc;n. Ng&agrave;y uống 1- 2 lần, mỗi lần 12 gr.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Trị vi&ecirc;m tuyến v&uacute;: 20 gr Kim ng&acirc;n hoa, 16 gr Cam thảo đất, 20 gr Bồ c&ocirc;ng nha, 20 gr Th&ocirc;ng thảo, 50 gr S&agrave;i đất, sắc uống ng&agrave;y một thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa cảm cúm: Kim ng&acirc;n 4g, Tía t&ocirc; 3g, Kinh giới 3g, Mạn kinh 2g, Gừng 3 lát. T&acirc;́t cả dùng lá phơi kh&ocirc;, sắc u&ocirc;́ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; &nbsp;Chữa sởi: Hoa kim ng&acirc;n 30g, cỏ Ban 30g. Dùng tươi, giã nhỏ, th&ecirc;m nước, gạn u&ocirc;́ng. Có th&ecirc;̉ phơi kh&ocirc;, sắc u&ocirc;́ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m ph&ocirc;̉i: Kim ng&acirc;n hoa, Sinh địa, Huy&ecirc;̀n s&acirc;m, m&ocirc;̃i vị 20g; Địa c&ocirc;́t bì, Sa s&acirc;m, Mạch m&ocirc;n, m&ocirc;̃i vị 16g; Hoàng li&ecirc;n 12g, Xương b&ocirc;̀ 6g. Sắc u&ocirc;́ng ngày 1 thang. Kim ng&acirc;n, Sinh địa, Huy&ecirc;̀n s&acirc;m, Mạch m&ocirc;n, m&ocirc;̃i vị 20g; Li&ecirc;n ki&ecirc;̀u, U&acirc;́t kim, Đan bì, m&ocirc;̃i vị 12g, Hoàng li&ecirc;n, Thạch xương b&ocirc;̀, m&ocirc;̃i vị 6g. Sắc u&ocirc;́ng, ngày, m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa áp xe ph&ocirc;̉i giai đoạn vi&ecirc;m nhi&ecirc;̃m, sung huy&ecirc;́t khởi phát: Kim ng&acirc;n, sài đ&acirc;́t, b&ocirc;̀ c&ocirc;ng anh, m&ocirc;̃i vị 20g; tang bạch bì, ý dĩ, m&ocirc;̃ vị 16g; kinh giới, hạnh nh&acirc;n, m&ocirc;̃i vị 12g. Sắc u&ocirc;́ng, ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; &nbsp;Chữa áp xe ph&ocirc;̉i giai đoạn toàn phát: Kim ng&acirc;n 20g; Hoàng đằng, &Yacute; dĩ, m&ocirc;̃i vị 16g; Li&ecirc;n ki&ecirc;̀u, Hoàng li&ecirc;n, Đào nh&acirc;n, m&ocirc;̃i vị 12g; đình lịch tử 8g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m gan virus (Ngũ linh thang gia giảm): Kim ng&acirc;n 16g; Nh&acirc;n tr&acirc;̀n 20g; Xa ti&ecirc;̀n 16g; Phục linh, ý dĩ, m&ocirc;̃i vị 12g; Trư linh, Trạch tả, đại phúc bì, m&ocirc;̃i vị 8g. Sắc u&ocirc;́ng, ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m gan mạn tính (Hoàng c&acirc;̀m hoạt thạch thang gia giảm): Kim ng&acirc;n 16g; nh&acirc;n tr&acirc;̀n 20g; hoàng c&acirc;̀m, hoạt thạch, đại phúc bì, m&ocirc;̣c th&ocirc;ng, m&ocirc;̃i vị 12g; phục linh, trư &nbsp;linh, đ&acirc;̣u kh&acirc;́u, m&ocirc;̃i vị 8g; cam thảo 4g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m c&acirc;̀u th&acirc;̣n c&acirc;́p tính: Kim ng&acirc;n, B&ocirc;̀ c&ocirc;ng anh, m&ocirc;̃i vị 20g; Mã đ&ecirc;̀ 12g; vỏ Quýt, vỏ r&ecirc;̃ D&acirc;u, vỏ cau kh&ocirc;, Ngũ gia bì, Qu&ecirc;́ chi, m&ocirc;̃i vị 8g, vỏ Gừng 6g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash;&nbsp;Chữa vi&ecirc;m khớp dạng th&acirc;́p (Bạch h&ocirc;̉ qu&ecirc;́ chi thang gia vị): Kim ng&acirc;n 20g; Thạch cao 40g; Tang chi, Ngạnh m&ecirc;̃, Hoàng bá, Phòng kỷ, mỗi vị 12g; Thương tru&acirc;̣t 8g; Qu&ecirc;́ chi 6g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; &nbsp;Chữa s&ocirc;́t xu&acirc;́t huy&ecirc;́t: Kim ng&acirc;n hoa, r&ecirc;̃ cỏ gianh, m&ocirc;̃i vị 20g; cỏ Nhọ n&ocirc;̀i, hoa Hoè, m&ocirc;̃i vi 16g; Li&ecirc;n ki&ecirc;̀u, Hoàng c&acirc;̀m, m&ocirc;̃i vị 12g; chi tử 8g. Sắc u&ocirc;́ng gày m&ocirc;̣t thang. N&ecirc;́u khát nước, th&ecirc;m Huy&ecirc;̀n s&acirc;m, Sinh địa (m&ocirc;̃i vị 12g); s&ocirc;́t cao, th&ecirc;m Tri m&acirc;̃u 8g.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa đinh r&acirc;u (Ngũ vị ti&ecirc;u đ&ocirc;̣c &acirc;̉m gia giảm): Kim ng&acirc;n hoa, b&ocirc;̀ c&ocirc;ng anh, tử hoa địa đinh, m&ocirc;̃i vị 40g; Cúc hoa, Li&ecirc;n ki&ecirc;̀u, m&ocirc;̃i vị 20g.<br />\r\nN&ecirc;́u s&ocirc;́t cao, tại ch&ocirc;̃ sưng đau nhi&ecirc;̀u th&ecirc;m thạch cao 40g; Hoàng c&acirc;̀m, Chi tử s&ocirc;́ng, Đan bì, m&ocirc;̃i vị 12g; Hoàng li&ecirc;n 8g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Vi&ecirc;m bạch mạch c&acirc;́p (Giải đ&ocirc;̣c đại thanh thang gia giảm): Kim ng&acirc;n, Đại thanh di&ecirc;̣p, Sinh địa, m&ocirc;̃i vị 40g; Huy&ecirc;̀n s&acirc;m, chi tử s&ocirc;́ng, m&ocirc;̃i vị 12g; mọc th&ocirc;ng 4g. N&ecirc;́u s&ocirc;́t cao th&ecirc;m Thạch cao 40g, Hoàng li&ecirc;n 4g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa nhi&ecirc;̃m khu&acirc;̉n huy&ecirc;́t (Thanh doanh thang gia giảm): Kim ng&acirc;n hoa, sinh địa, m&ocirc;̃i vị 40g, huy&ecirc;̀n s&acirc;m, li&ecirc;n ki&ecirc;̀u, m&ocirc;̃i vị 20g; địa c&ocirc;́t bì, đan bì, tri m&acirc;̃u, mạch m&ocirc;n, m&ocirc;̃i vị 12g; hoàng li&ecirc;n 6g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m ph&ocirc;̉i trẻ em: Kim ng&acirc;n hoa 16g; Thạch cao 20g; Tang bạch bì 8g; Tri m&acirc;̃u, Hoàng li&ecirc;n, Li&ecirc;n ki&ecirc;̀u, Hoàng c&acirc;̀m, m&ocirc;̃i vị 6g; Cam thảo 4g. Sắc u&ocirc;́ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa co gi&acirc;̣t trẻ em (Hương nhu &acirc;̉m gia giảm): Kim ng&acirc;n hoa 16g, hương nhu, bi&ecirc;̉n đ&acirc;̣u, m&ocirc;̃i vị 12g; h&acirc;̣u phác, li&ecirc;n ki&ecirc;̀u, m&ocirc;̃i vị 8g. Sắc u&ocirc;́ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m ph&acirc;̉n phụ c&acirc;́p tính: Kim ng&acirc;n, Li&ecirc;n ki&ecirc;̀u, Tỳ giải, &Yacute; dĩ, m&ocirc;̃i vị 16g; Hoàng bá, Hoàng li&ecirc;n, Mã đ&ecirc;̀, Nga tru&acirc;̣t, m&ocirc;̃i vị 12g; U&acirc;́t kim, Tam lăng, m&ocirc;̃i vị 8g, Đại hoàng 4g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Chữa vi&ecirc;m màng ti&ecirc;́p hợp c&acirc;́p: Kim ng&acirc;n 16g; Li&ecirc;n ki&ecirc;̀u,Hoàng c&acirc;̀m, Ngưu bàng tử, m&ocirc;̃i vị 12g; Chi tử 8g; Bạc hà, Cát cánh, m&ocirc;̃i vị 6g. Sắc u&ocirc;́ng ngày m&ocirc;̣t thang.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ndash; Trị chứng b&eacute;o ph&igrave; k&egrave;m cao huyết &aacute;p, rối loạn lipid m&aacute;u thuộc thể vị nhiệt (triệu chứng hoa mắt, ch&oacute;ng mặt, nặng đầu, mỏi tay ch&acirc;n, kh&aacute;t nước): Lấy hoa Kim ng&acirc;n, hoa C&uacute;c, Sơn tra mỗi vị 10 gr, h&atilde;m khoảng 20 ph&uacute;t với nước s&ocirc;i uống thay tr&agrave; trong ng&agrave;y.</span></span></p>\r\n', '1689527916_content_blogs.jpg', '2023-07-17 00:18:36', 0, 1);

